public void jumpRef()
{
    MenuFunction menuFunction;
    Args args = new Args();
    FormRun formRun;

    if (PaymCalendarModuleType == PaymCalendarModuleType::Customer)
    {
        menuFunction = new MenuFunction(menuItemDisplayStr(CustPaymTerm), MenuItemType::Display);
    }
    else
    {
        menuFunction = new MenuFunction(menuItemDisplayStr(PaymTerm), MenuItemType::Display);
    }

    // Can't use menuFunction.run() as we need to wait for modal pop-up to close
    formRun = menuFunction.create(args);
    formRun.run();
    formRun.wait(true);
}

