public void clicked()
{
    PaymCalendarCountryRegion localPaymCalendarCountryRegion;
    PaymCalendarCountryRegion currentPaymCalendarCountryRegion;
    PaymCalendarCountryRegionLegalEntity localPaymCalendarCountryRegionLegalEntity;
    boolean isFlushCache;

    // Copy the current record details since we have to highlight the selected record after enabling
    // all the main accounts.
    currentPaymCalendarCountryRegion.data(paymCalendarCountryRegion);
    localPaymCalendarCountryRegion = paymCalendarCountryRegion_ds.getFirst() as PaymCalendarCountryRegion;

    ttsbegin;

    while (localPaymCalendarCountryRegion != null)
    {
        select firstonly RecId from localPaymCalendarCountryRegionLegalEntity
            where localPaymCalendarCountryRegionLegalEntity.PaymCalendarCountryRegion == localPaymCalendarCountryRegion.RecId
                && localPaymCalendarCountryRegionLegalEntity.ModuleType == paymCalendarModuleType;

        if (!localPaymCalendarCountryRegionLegalEntity)
        {
            localPaymCalendarCountryRegionLegalEntity.PaymCalendarCountryRegion = localPaymCalendarCountryRegion.RecId;
            localPaymCalendarCountryRegionLegalEntity.ModuleType = paymCalendarModuleType;
            localPaymCalendarCountryRegionLegalEntity.IncludeStateHoliday = StateHoliday::existsForCountryRegion(localPaymCalendarCountryRegion.CountryRegion);
            localPaymCalendarCountryRegionLegalEntity.doInsert();
            isFlushCache = true;
        }

        localPaymCalendarCountryRegion = paymCalendarCountryRegion_ds.getNext() as PaymCalendarCountryRegion;
    }

    if (isFlushCache)
    {
        PaymCalendarProcessor::calendarForLocationChanged();
    }

    ttscommit;

    super();

    paymCalendarCountryRegion_ds.research();
    paymCalendarCountryRegion_ds.positionToRecord(currentPaymCalendarCountryRegion);
    paymCalendarCountryRegion_ds.refresh();
}

public void clicked()
{
    PaymCalendarCountryRegion localPaymCalendarCountryRegion;
    PaymCalendarCountryRegion currentPaymCalendarCountryRegion;
    PaymCalendarCountryRegionLegalEntity localPaymCalendarCountryLegalEntity;

    // Copy the current record details since we have to highlight the selected record after enabling
    // all the main accounts.
    currentPaymCalendarCountryRegion.data(paymCalendarCountryRegion);
    localPaymCalendarCountryRegion = paymCalendarCountryRegion_ds.getFirst() as PaymCalendarCountryRegion;

    ttsbegin;

    while (localPaymCalendarCountryRegion != null)
    {
        delete_from localPaymCalendarCountryLegalEntity
            where localPaymCalendarCountryLegalEntity.PaymCalendarCountryRegion == localPaymCalendarCountryRegion.RecId
                && localPaymCalendarCountryLegalEntity.ModuleType == paymCalendarModuleType;

        localPaymCalendarCountryRegion = paymCalendarCountryRegion_ds.getNext() as PaymCalendarCountryRegion;
    }

    ttscommit;

    super();

    paymCalendarCountryRegion_ds.research();
    paymCalendarCountryRegion_ds.positionToRecord(currentPaymCalendarCountryRegion);
    paymCalendarCountryRegion_ds.refresh();
}

public boolean modified()
{
    boolean ret;
    boolean stateHolidayExists;
    PaymCalendarCountryRegion currentPaymCalendarCountryRegion;

    currentPaymCalendarCountryRegion.data(paymCalendarCountryRegion);

    ret = super();

    stateHolidayExists = StateHoliday::existsForCountryRegion(paymCalendarCountryRegion.CountryRegion);
    legalEntityCountryRegion_IncludeStateHoliday.allowEdit(stateHolidayExists);

    paymCalendarCountryRegion_ds.research();
    paymCalendarCountryRegion_ds.positionToRecord(currentPaymCalendarCountryRegion);
    paymCalendarCountryRegion_ds.refresh();

    return ret;
}
