public void init()
{
    super();

    payrollAccrualEarningCode_ds.query().dataSourceTable(tableNum(PayrollEarningCode)).addSortField(fieldNum(PayrollEarningCode, EarningCode));

    element.updateDesign();
}

public void updateDesign()
{
    // This entire fasttab only displays for Hour or Compensatory
    accrueEarningCode.visible(payrollAccrual.AccrualMethod == PayrollAccrualMethod::Hourly
        || payrollAccrual.AccrualMethod == PayrollAccrualMethod::Compensatory);

    // Set default visibility and editability control properties
    payrollAccrualRate_ds.object(fieldNum(PayrollAccrualRate, CarryForwardLimit)).visible(payrollAccrual.CanCarryForward);

    // Set method based defaults and control property restrictions.
    switch (payrollAccrual.AccrualMethod)
    {
        case PayrollAccrualMethod::Hourly:

            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, AccuralType)).visible(true);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCodeGroup)).visible(true);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCode)).visible(true);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, AccrualRate)).visible(false);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, WorkerAccrualDateMethod)).visible(false);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, CustomAccrualDate)).visible(false);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, Frequency)).visible(false);
            payrollAccrualRate_ds.object(fieldNum(PayrollAccrualRate, HourlyRate)).visible(true);
            break;

        case PayrollAccrualMethod::FlatAmount:

            payrollAccrual_ds.object(fieldNum(PayrollAccrual, Frequency)).visible(true);
            payrollAccrualRate_ds.object(fieldNum(PayrollAccrualRate, HourlyRate)).visible(true);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, WorkerAccrualDateMethod)).visible(false);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, CustomAccrualDate)).visible(false);

            if (payrollAccrual.Frequency == 0)
            {
                payrollAccrual.Frequency = PayrollCalculationFrequency::findDefaultCalculationFrequency().RecId;
            }
            break;

        case PayrollAccrualMethod::WorkerDate:

            payrollAccrual_ds.object(fieldNum(PayrollAccrual, CustomAccrualDate)).visible(payrollAccrual.WorkerAccrualDateMethod == PayrollWorkerAccrualDateMethod::Custom);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, WorkerAccrualDateMethod)).visible(true);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, Frequency)).visible(false);
            payrollAccrualRate_ds.object(fieldNum(PayrollAccrualRate, HourlyRate)).visible(true);

            if (payrollAccrual.WorkerAccrualDateMethod == PayrollWorkerAccrualDateMethod::Custom && payrollAccrual.CustomAccrualDate == dateNull())
            {
                payrollAccrual_ds.object(fieldNum(PayrollAccrual, CustomAccrualDate)).setValue(mkDate(01, 01, year(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))));
            }
            break;

        case PayrollAccrualMethod::Compensatory:

            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, AccuralType)).visible(false);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCodeGroup)).visible(false);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCode)).visible(true);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCode)).enabled(true);
            payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, AccrualRate)).visible(true);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, WorkerAccrualDateMethod)).visible(false);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, CustomAccrualDate)).visible(false);
            payrollAccrual_ds.object(fieldNum(PayrollAccrual, Frequency)).visible(false);
            payrollAccrualRate_ds.object(fieldNum(PayrollAccrualRate, HourlyRate)).visible(false);
            break;
    }
}

public void refreshBasisEarningGrid()
{
    if (payrollAccrual.AccrualMethod == PayrollAccrualMethod::Hourly)
    {
        // Set enable states for Hourly
        payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCode)).enabled(payrollAccrualBasisEarningcode.AccuralType == PayrollAccrualType::EarningCode);
        payrollAccrualBasisEarningcode_ds.object(fieldNum(PayrollAccrualBasisEarningcode, EarningCodeGroup)).enabled(payrollAccrualBasisEarningcode.AccuralType == PayrollAccrualType::EarningCodeGroup);

        // We allow changes to the line type. When changed, the other rerference needs to be cleared.
        if (payrollAccrualBasisEarningcode.AccuralType == PayrollAccrualType::EarningCode)
        {
            if (payrollAccrualBasisEarningcode.EarningCodeGroup != 0)
            {
                payrollAccrualBasisEarningcode.EarningCodeGroup = 0;
            }
        }
        else if (payrollAccrualBasisEarningcode.AccuralType == PayrollAccrualType::EarningCodeGroup)
        {
            if (payrollAccrualBasisEarningcode.EarningCode != 0)
            {
                payrollAccrualBasisEarningcode.EarningCode = 0;
            }
        }
    }
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(PayrollAccrual)).addSortField(fieldNum(PayrollAccrual, AccrualId), SortOrder::Ascending);
}

public void selectionChanged()
{
    super();

    // Based on record context, control Enable/Disable & visibility value properties potentially change.
    element.updateDesign();
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(PayrollAccrualRate)).addSortField(fieldNum(PayrollAccrualRate, EmploymentMonths), SortOrder::Ascending);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    ret = PayrollEarningCode::lookupEarningCodeForAccrual(_formReferenceControl);

    return ret;
}

public void selectionChanged()
{
    super();

    if (payrollAccrual.AccrualMethod == PayrollAccrualMethod::Hourly)
    {
        // Refresh grid controls.   Earning code and Earning Group
        // selections are both available.  They are mutually exclusive.
        element.refreshBasisEarningGrid();
    }
}

public display PayrollEarningCodeGroupDescription displayDescription(PayrollAccrualBasisEarningcode _earningBaseCode)
{
    PayrollEarningCode earningCodeLocal;
    PayrollEarningCodeGroup earningGroup;
    PayrollEarningCodeGroupDescription description;

    if (_earningBaseCode.EarningCode != 0)
    {
        earningCodeLocal = PayrollEarningCode::find(_earningBaseCode.EarningCode);
        description = earningCodeLocal.Description;
    }
    else if (_earningBaseCode.EarningCodeGroup != 0)
    {
        earningGroup = PayrollEarningCodeGroup::find(_earningBaseCode.EarningCodeGroup);
        description = earningGroup.Description;
    }

    return description;
}

public void modified()
{
    super();

    // Refresh grid control Enable/Disable settings.
    element.refreshBasisEarningGrid();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    ret = PayrollEarningCode::lookupEarningCodeForAccrual(_formReferenceControl);

    return ret;
}
