/// <summary>
/// Determines whether the line can be edited or removed.
/// </summary>
/// <param name="_formDataSource">
/// A form data source of table that extends <c>PayrollPayStatementLine</c> table.
/// </param>
/// <returns>
///    true if the line can be removed or edited; otherwise, false.
/// </returns>
public boolean allowEditOrRemoveLine(FormDataSource _formDataSource)
{
    boolean allowEditOrDelete = true;
    PayrollPayStatementLine  selectedLine = _formDataSource.getFirst(1);

    if (_formDataSource.table() != tableNum(PayrollPayStatementTaxLine)
        && _formDataSource.table() != tableNum(PayrollPayStatementBenefitLine))
    {
        // The expected datasource was not provided; return false;
        allowEditOrDelete = false;
    }
    else if (payrollPayStatement.PaymentType != PayrollPaymentType::Manual)
    {
        if (selectedLine)
        {
            while (selectedLine)
            {
                if (selectedLine.IsLineLocked)
                {
                    allowEditOrDelete = false;
                    break;
                }

                selectedLine = _formDataSource.getNext();
            }
        }
        else
        {
            selectedLine = _formDataSource.cursor();
            allowEditOrDelete = !selectedLine.IsLineLocked;
        }
    }

    return allowEditOrDelete;
}

