public void createBenefitLine(AmountMST _accountingCurrencyAmount,
    HcmBenefitRecId _benefitRecId,
    HcmPositionRecId _positionRecId,
    PayrollType _payrollType)
{
    AccountingDistributionTemplate distributionTemplate;
    PayrollPayStatementBenefitLine  benefitLine;
    RefRecId accountingDistTemplate;
    Map accountingDistTemplateDetailMap;
    boolean doAccounting = (payrollPayStatement.SourceDocumentHeader != 0);

    if (doAccounting)
    {
        accountingDistTemplate = PayrollPayStatementLine::createAccountingDistributionTemplate();
        accountingDistTemplateDetailMap = PayrollPayStatementLine::createAccountingDistributionDetail(payrollPayStatement, accountingDistTemplate);
    }

    benefitLine = PayrollPayStatementBenefitLine::create(payrollPayStatement,
            payrollPayPeriod.PeriodEndDate,
            HcmBenefit::find(_benefitRecId),
            accountingDistTemplate,
            _accountingCurrencyAmount,
            _payrollType,
            _positionRecId,
            PayrollPayStatementLineSource::Manual,
            NoYes::Yes);

    if (doAccounting)
    {
        // Reread the buffer to ensure any source document updates are in place
        benefitLine.reread();

        benefitLine.createProjectAccountingDistribution(accountingDistTemplateDetailMap);

        delete_from distributionTemplate
            where distributionTemplate.RecId == accountingDistTemplate;
    }
}

public void createTaxLine(AmountMST _accountingCurrencyAmount,
    PayrollTaxCode _payrollTaxCode)
{
    AccountingDistributionTemplate distributionTemplate;
    PayrollPayStatementTaxLine     taxLine;
    RefRecId accountingDistTemplate;
    Map accountingDistTemplateDetailMap;
    boolean doAccounting = (payrollPayStatement.SourceDocumentHeader != 0);

    if (doAccounting)
    {
        accountingDistTemplate = PayrollPayStatementLine::createAccountingDistributionTemplate();
        accountingDistTemplateDetailMap = PayrollPayStatementLine::createAccountingDistributionDetail(payrollPayStatement, accountingDistTemplate);
    }

    taxLine = PayrollPayStatementTaxLine::create(payrollPayStatement,
            payrollPayPeriod.PeriodEndDate,
            _payrollTaxCode,
            accountingDistTemplate,
            _accountingCurrencyAmount,
            PayrollPayStatementLineSource::Manual,
            NoYes::Yes);

    if (doAccounting)
    {
        taxLine.createProjectAccountingDistribution(accountingDistTemplateDetailMap);

        delete_from distributionTemplate
            where distributionTemplate.RecId == accountingDistTemplate;
    }
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;
    QueryFilter queryFilter;

    #ISOCountryRegionCodes

    super();

    info("@PayrollProcessing:PayrollDeprecationWarningMessage");

    taxCodeLookup = PayrollTaxCodeLookup::newByPayStatementLocked();
    taxCodeLookup.parmFilterByIsEmployerTax(true);
    taxCodeLookup.parmCountryRegionId(LogisticsAddressCountryRegion::findByISOCode(#isoUS).CountryRegionId);
    taxCodeLookup.parmIsPaymentTypeManual(false);

    isBudgetControlEnabled = PayrollPayStatement::isBudgetControlEnabledForPayStatement();

    queryBuildDataSource = PayrollPayStatement_DS.queryBuildDataSource();

    PayrollPayStatement_DS.query().addQueryFilter(queryBuildDataSource, fieldstr(PayrollPayStatement, Worker));
    PayrollPayStatement_DS.query().addQueryFilter(queryBuildDataSource, fieldstr(PayrollPayStatement, PaymentDate));
    PayrollPayStatement_DS.query().addQueryFilter(queryBuildDataSource, fieldstr(PayrollPayStatement, PaymentMethodType));
    PayrollPayStatement_DS.query().addQueryFilter(queryBuildDataSource, fieldstr(PayrollPayStatement, PaymentType));

    queryBuildDataSource.addSortField(fieldNum(PayrollPayStatement, DocumentNumber),SortOrder::Descending);
}

