public void performBudgetCheck(PayrollPayStatementLine _payStatementLine)
{
    boolean doAccounting;

    if (payrollPayStatement.PaymentType == PayrollPaymentType::Manual
        && payrollPayStatement.SourceDocumentHeader == 0)
    {
        doAccounting = false;
    }
    else
    {
        doAccounting = true;
    }

    if (doAccounting)
    {
        // Submit to source document processor for an on-demand GL budget check
        SourceDocumentProcessorFacade::submitOnDemandSourceDocLineImpl(_payStatementLine);
    }
}

public void refreshHeaderForRecalculation()
{
    // Update the gross and net amount fields on the header buffer
    element.updatePayStatementTotals();

    if (payrollPayStatement.RecId)
    {
        payrollPayStatement.PaymentStatus = PayrollPaymentStatus::RequiresRecalculation;
        payrollPayStatement_ds.write();
    }

    payrollPayStatement_ds.active();
}

public void refreshPayStatement(PayrollPayStatementRecId _payrollPayStatementRecId = 0)
{
    if (_payrollPayStatementRecId != 0)
    {
        element.args().lookupRecord(PayrollPayStatement::find(_payrollPayStatementRecId));
        payrollPayStatement_ds.research();
    }
    else
    {
        payrollPayStatement_ds.research(true);
    }
}

