public void updateBenefitLinesAsModified(HcmBenefitRecId _benefitRecId, NoYes _isEmployer)
{
    PayrollPayStatementBenefitLine benefitLine;

    // Once the user has manually updated a line, it is considered a Manual sourced line
    update_recordset benefitLine
        setting IsPayStatementLineOverridden = NoYes::Yes,
                Source = PayrollPayStatementLineSource::Manual
        where benefitLine.Benefit == _benefitRecId
        &&    benefitLine.IsEmployer == _isEmployer
        &&    benefitLine.PayStatement == payrollPayStatement.RecId;
}

