/// <summary>
/// Sets the visibility of the messages in case of recalculation required
/// or benefit accrual calculations or reversed pay statement.
/// </summary>
public void updateMessages()
{
    str         reversalMessage, calculationMessage;
    boolean     isReversalMessageVisible = false;

    AccrualMessageText.visible(false);

    PayrollPayStatement_IsReversed.visible(false);
    PayrollPayStatement_ReversedPayStatement.visible(false);

    switch (payrollPayStatement.PaymentStatus)
    {
        case PayrollPaymentStatus::Calculated:
        case PayrollPaymentStatus::RequiresRecalculation:

            if (!payrollPayStatement.IsReversed)
            {
                // The pay statement has not been submitted.
                AccrualMessageText.text("@SYS4090123");
                AccrualMessageText.visible(true);
            }

            // Update the button text and message text
            if (payrollPayStatement.PaymentStatus == PayrollPaymentStatus::RequiresRecalculation)
            {
                if (payrollPayStatement.PaymentType == PayrollPaymentType::Manual && PayrollPayStatement.CreationType == PayrollPayStatementCreationType::Generated)
                {
                    CalculateFinalizeButton.text("@SYS4000418");
                    calculationMessage = strfmt("@SYS4000417", PayrollPayStatement.DocumentNumber);
                }
                else
                {
                    CalculateFinalizeButton.text("@SYS117");
                    calculationMessage = strfmt("@Payroll:PayStatement1MustBeRecalculatedBecauseItHasBeenChanged", PayrollPayStatement.DocumentNumber);
                }
            }
            break;

        case PayrollPaymentStatus::PaymentIssued:
        case PayrollPaymentStatus::SentToPaymentJournal:

            // The pay statement has been submitted.
            if (!PayrollPayStatementAccrualBalance::doesPayStatementAccrualBalanceExist(payrollPayStatement.RecId))
            {
                // Determine why accrual balances for this pay statment do not exist.
                if (!PayrollWorkerEnrolledAccrual::doesWorkerEnrollmentExist(payrollPayStatement.Worker))
                {
                    // The worker is not currently enrolled in any accrual plans.
                    AccrualMessageText.text("@SYS4090124");
                    AccrualMessageText.visible(true);
                }
                else if (!PayrollWorkerEnrolledAccrual::doesActiveWorkerEnrollmentExist(payrollPayStatement.Worker))
                {
                    // The worker's enrolled accrual plans are not active.
                    AccrualMessageText.text("@SYS4090125");
                    AccrualMessageText.visible(true);
                }
            }
            break;
    }

    if (payrollPayStatement.IsReversed)
    {
        isReversalMessageVisible = true;
        if (payrollPayStatement.ReversedPayStatement)
        {
            reversalMessage = strFmt("@SYS4090150", payrollPayStatement.DocumentNumber, PayrollPayStatement::find(payrollPayStatement.ReversedPayStatement).DocumentNumber);
            PayrollPayStatement_ReversedPayStatement.visible(true);
        }
        else
        {
            reversalMessage = strfmt("@SYS4090151", payrollPayStatement.DocumentNumber);
            PayrollPayStatement_IsReversed.visible(true);
        }
    }

    if (payrollPayStatement.PaymentStatus == PayrollPaymentStatus::RequiresRecalculation)
    {
        warning(calculationMessage);
    }

    if (isReversalMessageVisible)
    {
        info(reversalMessage);
    }
}

/// <summary>
///     Updates the form's <c>PayrollPayStatement</c> table buffer to have the current
///     gross and net totals, according to its lines' values.
/// </summary>
public void updatePayStatementTotals()
{
    PayrollStatementAccountingCurrencyAmount netAmount;
    PayrollPayStatementEarningLine earningLines;
    PayrollPayStatementBenefitLine benefitLines;
    PayrollPayStatementTaxLine taxLines;

    select sum(AccountingCurrencyAmount) from earningLines
        where earningLines.PayStatement == payrollPayStatement.RecId
        &&    earningLines.IsMarkedForRemoval == NoYes::No;

    select sum(AccountingCurrencyAmount) from taxLines
        where taxLines.PayStatement == payrollPayStatement.RecId
        &&    taxLines.IsEmployer == NoYes::No
        &&    taxLines.IsMarkedForRemoval == NoYes::No;

    select sum(AccountingCurrencyAmount) from benefitLines
        where benefitLines.PayStatement == payrollPayStatement.RecId
        &&    benefitLines.IsEmployer == NoYes::No
        &&    benefitLines.IsMarkedForRemoval == NoYes::No;

    // Update the pay statement buffer with the correct gross amount
    payrollPayStatement.GrossAccountingCurrencyAmount = CurrencyExchange::round(earningLines.AccountingCurrencyAmount, PayrollSetup::currencyCode());

    // Update the pay statement buffer with the correct net amount
    netAmount = payrollPayStatement.GrossAccountingCurrencyAmount - taxLines.AccountingCurrencyAmount - benefitLines.AccountingCurrencyAmount;
    payrollPayStatement.NetAccountingCurrencyAmount = CurrencyExchange::round(netAmount, PayrollSetup::currencyCode());
}

public void updateTaxLinesAsModified(PayrollTaxCodeRecId _taxCodeRecId, NoYes _isEmployer)
{
    PayrollPayStatementTaxLine taxLine;

    update_recordset taxLine
        setting IsPayStatementLineOverridden = NoYes::Yes
        where taxLine.TaxCode == _taxCodeRecId
        &&    taxLine.IsEmployer == _isEmployer
        &&    taxLine.IsPayStatementLineOverridden == NoYes::No
        &&    taxLine.PayStatement == payrollPayStatement.RecId;
}

