public boolean validateAccountingCurrency(PayrollPayStatementLine _lineDataSource, boolean _isEmployer)
{
    boolean isValid = true;

    if (!_lineDataSource.AccountingCurrencyAmount)
    {
        if (_isEmployer)
        {
            isValid = checkFailed(strFmt("@SYS26332","@Payroll:ContributionAmount"));
        }
        else
        {
            isValid = checkFailed(strFmt("@SYS26332","@Payroll:DeductionAmount"));
        }
    }

    return isValid;
}

