public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // This validation is intended to run if user try to change Payment method type ot Electronic payment.
    if (ret && payrollPayStatement.orig().PaymentMethodType != payrollPayStatement.PaymentMethodType
            && payrollPayStatement.PaymentMethodType == PayrollPaymentMethodType::ElectronicPayment)
    {
        ret = PayrollPayStatement::isAccountExistsForElectronicPayment(payrollPayStatement.Worker, CompanyInfo::current(), true);
    }

    return ret;
}

public void init()
{
    PayrollTaxTransactionHistoryUnion   payrollTaxTransactionHistoryUnion;
    PayrollUSTaxTransactionHistory      payrollUSTaxTransactionHistory;
    PayrollPayStatementRecId            payrollPayStatementRecId;
    QueryBuildRange                     qbr;

    super();

    if (element.args().menuItemType() == MenuItemType::Display
        && element.args().menuItemName() == menuitemDisplayStr(PayrollTaxTransHistoryPayStatement))
    {
        switch (element.args().dataset())
        {
            case tableNum(PayrollTaxTransactionHistoryUnion):
                payrollTaxTransactionHistoryUnion = element.args().record() as PayrollTaxTransactionHistoryUnion;
                if (payrollTaxTransactionHistoryUnion.RecId != 0)
                {
                    payrollPayStatementRecId = payrollTaxTransactionHistoryUnion.PayStatement;
                }
                break;

            case tableNum(PayrollUSTaxTransactionHistory):
                payrollUSTaxTransactionHistory = element.args().record() as PayrollUSTaxTransactionHistory;
                if (payrollUSTaxTransactionHistory.RecId != 0)
                {
                    payrollPayStatementRecId = payrollUSTaxTransactionHistory.PayStatement;
                }
                break;
        }

        if (payrollPayStatementRecId != 0)
        {
            qbr = payrollPayStatement_ds.query().dataSourceTable(tableNum(PayrollPayStatement)).addRange(fieldNum(PayrollPayStatement, RecId));
            qbr.value(queryValue(payrollPayStatementRecId));
            qbr.status(RangeStatus::Hidden);
        }
    }
}

