public void removeEarningLine()
{
    int numEarningLines;
    PayrollPayStatementEarningLine earningLine;

    select count(RecId) from earningLine
        where earningLine.PayStatement == payrollPayStatement.RecId
        &&    earningLine.IsMarkedForRemoval == NoYes::No;
    numEarningLines = int642int(earningLine.RecId);

    if (numEarningLines == 1)
    {
        // User is attempting to delete all earning lines from the pay statement
        checkFailed("@Payroll:APayStatementMustContainAtLeastOneEarningLineIfAllEarningLin");
    }
    else
    {
        payrollPayStatementEarningLine.IsMarkedForRemoval = NoYes::Yes;
    }
}

