public void selectionChanged()
{
    boolean allowEditOrDelete;

    super();

    if (payrollPayStatement_ds.allowEdit())
    {
        allowEditOrDelete = element.allowEditOrRemoveLine(payrollPayStatementTaxContributionLine_ds);

        payrollPayStatementTaxContributionLine_ds.allowEdit(allowEditOrDelete);
        payrollPayStatementTaxContributionLine_ds.allowDelete(allowEditOrDelete);
    }
}

public void write()
{
    if (!payrollPayStatementTaxContributionLine.RecId)
    {
        // Mark any tax contributions originally created as modified
        element.updateTaxLinesAsModified(payrollPayStatementTaxContributionLine.TaxCode, NoYes::Yes);

        element.createTaxLine(payrollPayStatementTaxContributionLine.AccountingCurrencyAmount,
            PayrollTaxCode::find(payrollPayStatementTaxContributionLine.TaxCode));

        payrollPayStatementTaxContributionLine.clear();
    }
    else
    {
        super();

        // Mark any tax contributions originally created as modified
        element.updateTaxLinesAsModified(payrollPayStatementTaxContributionLine.TaxCode, NoYes::Yes);
    }

    element.refreshHeaderForRecalculation();
}

