public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    PayrollTaxCodeId taxCode;

    taxCodeLookup.parmIsEmployerTax(NoYes::Yes);
    taxCodeLookup.parmIsPaymentTypeManual(payrollPayStatement.PaymentType == PayrollPaymentType::Manual);

    ret = super(_formReferenceControl);

    taxCode = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(PayrollTaxCode, Code), tableStr(PayrollTaxCode))).value();

    if (ret != null && !taxCodeLookup.validateTaxCode(PayrollTaxCode::findByCodeCountryRegion(taxCode, taxCodeLookup.parmCountryRegionId()).RecId))
    {
        ret = null;
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    taxCodeLookup.parmIsEmployerTax(NoYes::Yes);
    taxCodeLookup.parmIsPaymentTypeManual(payrollPayStatement.PaymentType == PayrollPaymentType::Manual);

    return taxCodeLookup.lookupTaxCode(_formReferenceControl);
}

public boolean validateWrite()
{
    boolean isvalid = true;

