        allowEditOrDelete = element.allowEditOrRemoveLine(payrollPayStatementTaxDeductionLine_ds);

        payrollPayStatementTaxDeductionLine_ds.allowEdit(allowEditOrDelete);
        payrollPayStatementTaxDeductionLine_ds.allowDelete(allowEditOrDelete);
    }
