public void write()
{
    if (!payrollPayStatementTaxDeductionLine.RecId)
    {
        // Mark any tax deductions originally created as modified
        element.updateTaxLinesAsModified(payrollPayStatementTaxDeductionLine.TaxCode, NoYes::No);

        element.createTaxLine(payrollPayStatementTaxDeductionLine.AccountingCurrencyAmount,
            PayrollTaxCode::find(payrollPayStatementTaxDeductionLine.TaxCode));

        payrollPayStatementTaxDeductionLine.clear();
    }
    else
    {
        super();

        // Mark any tax deductions originally created as modified
        element.updateTaxLinesAsModified(payrollPayStatementTaxDeductionLine.TaxCode, NoYes::No);
    }

    element.refreshHeaderForRecalculation();
}

