public void delete()
{
    if (!payrollPayStatementBenefitConLine)
    {
        super();
    }
    else
    {
        // We only want to set IsMarkedForRemoval=Yes on the line
        if (payrollPayStatementBenefitConLine.canRemoveRecord())
        {
            this.removeBenefitContributionLine();
            this.research(false);
        }
        else
        {
            //
            // Since the record couldn't be removed we need to unmark the record to make
            // sure the delete method is not called again.
            //
            payrollPayStatementBenefitConLine_ds.mark(0);
        }
    }
}

public void removeBenefitContributionLine()
{
    payrollPayStatementBenefitConLine.IsMarkedForRemoval = NoYes::Yes;
    payrollPayStatementBenefitConLine.IsPayStatementLineOverridden = NoYes::Yes;

    // This is a soft delete of the benefit line and therefore the record is updated instead of deleted
    ttsBegin;
    payrollPayStatementBenefitConLine_ds.write();
    ttsCommit;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    if (isBudgetControlEnabled)
    {
        element.cacheAddBudgetDisplayMethods(this);
    }

    qbds = this.query().dataSourceName(this.name());
    qbds.addRange(fieldNum(PayrollPayStatementBenefitLine, IsEmployer)).value(queryValue(NoYes::Yes));
    qbds.addRange(fieldNum(PayrollPayStatementBenefitLine, IsMarkedForRemoval)).value(queryValue(NoYes::No));

    qbds.sortClear();
    qbds.addSortField(fieldNum(PayrollPayStatementBenefitLine, Benefit), SortOrder::Ascending);
}

