public void write()
{
    if (!payrollPayStatementBenefitConLine.RecId)
    {
        // Mark any benefit contributions originally created as modified
        element.updateBenefitLinesAsModified(payrollPayStatementBenefitConLine.Benefit, NoYes::Yes);

        element.createBenefitLine(payrollPayStatementBenefitConLine.AccountingCurrencyAmount,
            payrollPayStatementBenefitConLine.Benefit,
            payrollPayStatementBenefitConLine.Position,
            PayrollType::Contribution);

        payrollPayStatementBenefitConLine.clear();
    }
    else
    {
        super();

        // Mark any benefit contributions originally created as modified
        element.updateBenefitLinesAsModified(payrollPayStatementBenefitConLine.Benefit, NoYes::Yes);
    }

    element.refreshHeaderForRecalculation();
}

