public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    HcmBenefitPlanId benefitPlanId;
    HcmBenefitOptionId benefitOptionId;
    HcmBenefitLookup benefitLookup = HcmBenefitLookup::newBenefits(periodStartDate, periodEndDate);

    ret = super(_formReferenceControl);

    benefitPlanId = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(HcmBenefitPlan, BenefitPlanID), tableStr(HcmBenefitPlan))).value();
    benefitOptionId = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(HcmBenefitOption, BenefitOptionID), tableStr(HcmBenefitOption))).value();

    benefitLookup.parmPayrollImpact(PayrollImpact::ContributionOnly);
    if (ret != null && !benefitLookup.validateBenefit(benefitPlanId, benefitOptionId))
    {
        ret = null;
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    HcmBenefitLookup benefitLookUp;

    benefitLookUp = HcmBenefitLookup::newBenefits(periodStartDate, periodEndDate);
    benefitLookUp.parmPayrollImpact(PayrollImpact::ContributionOnly);
    ret = benefitLookUp.lookupBenefit(_formReferenceControl);

    return ret;
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common              ret;
    HcmPositionLookup   positionLookup  = HcmPositionLookup::newAllPositions();
    HcmPositionId       positionId;

    ret = super(_formReferenceControl);

    positionId = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(HcmPosition, PositionId), tableStr(HcmPosition))).value();

    if (ret != null && !positionLookup.validatePositionWorkerLegalEntity(HcmPosition::findByPosition(positionId).RecId, payrollPayStatement.Worker, payrollPayPeriod.PayCycle, CompanyInfo::current(), true, true))
    {
        ret = null;
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    HcmPositionLookup   positionLookup  = HcmPositionLookup::newAllPositions();

    return positionLookup.lookupPositionWorkerLegalEntity(_formReferenceControl, payrollPayStatement.Worker, payrollPayPeriod.PayCycle, CompanyInfo::current(), true, true, true);
}

