public void clicked()
{
    super();
    PayrollPayStatement_Ds.research(true);
}

public void clicked()
{
    super();

    // We want to force save the record before recalculation updates the header.
    payrollPayStatement_ds.leaveRecord();

    // Initiate the recalculation process for the current pay statement
    PayrollPayStatementGenerationTask task = new PayrollPayStatementGenerationTask();
    task.initializeFromPayment(payrollPayStatement);
    task.runOperation();

    payrollPaystatement_ds.research(true);
    payrollpaystatement_ds.refresh();
}

public void clicked()
{
    PayrollPayStatementLine payStatementLine, payStatementLineBase;

    if (isBudgetControlEnabled && payrollPayStatement && PayrollPayStatement.SourceDocumentHeader)
    {
        ttsbegin;
        while select payStatementLine
            where payStatementLine.PayStatement == payrollPayStatement.RecId
            &&    payStatementLine.IsMarkedForRemoval == NoYes::No
        {
            //
            // This is required in order for the SourceDocumentLineImplementation map to correctly
            // hook up to the PayrollPayStatementLine table since the table participates in
            // table inheritance.
            //
            payStatementLineBase.data(payStatementLine);

            // Submit to source document processor for an on-demand GL budget check
            SourceDocumentProcessorFacade::submitOnDemandSourceDocLineImpl(payStatementLineBase);
        }
        ttscommit;

        payrollPayStatement_ds.updateBudgetCheckResultDisplay();
    }
}

