public void addLERangeOnTaxAccounting()
{
    QueryBuildRange             rangeTaxAccountingLE;

    rangeTaxAccountingLE =  SysQuery::findOrCreateRange(payrollTaxAccountingRule_ds.query().dataSourceTable(tableNum(PayrollTaxAccountingRule)), fieldNum(PayrollTaxAccountingRule, LegalEntity));
    rangeTaxAccountingLE.value(queryValue(selectedDataAreaRecId));

    payrollTaxAccountingRule_ds.executeQuery();
}

