    rangeTaxCodeDetailLE =  SysQuery::findOrCreateRange(payrollTaxCodeDetail_ds.query().dataSourceTable(tableNum(PayrollTaxCodeDetail)), fieldNum(PayrollTaxCodeDetail, LegalEntity));
    rangeTaxCodeDetailLE.value(queryValue(selectedDataAreaRecId));
}

