public void init()
{
    super();

    //Initialize splitter

    legalEntityComboBox.text(curext());
    selectedLegalEntityDataAreaId = legalEntityComboBox.text();
    selectedDataAreaRecId = CompanyInfo::findDataArea(legalEntityComboBox.text()).RecId;
    selectedLegalEntity = legalEntityComboBox.text();

    DimensionEntryControl.parmCompany(legalEntityComboBox.text());
    LedgerDimension.parmDataAreaId(CompanyInfo::findRecId(selectedDataAreaRecId).DataArea);

    element.addLERangeOnTaxCodeDetail();
    element.addLERangeOnTaxAccounting();
}

public void openMaintainTaxCodeVersions()
{
    Args                args;
    MenuFunction        menuFunction;
    FormRun             formRun;

    menuFunction =  new MenuFunction(menuitemDisplayStr(PayrollTaxCodeDateManager), MenuItemType::Display);

    args = new Args(menuFunction.object());
    args.caller(element);
    args.openMode(OpenMode::View);
    args.lookupTable(tableNum(PayrollTaxCode));
    args.lookupField(fieldNum(PayrollTaxCode, RecId));
    args.lookupValue(int642str(payrollTaxCode.RecId));

    formRun = ClassFactory::formRunClassOnClient(args);
    formRun.init();
    formRun.run();
    formRun.wait(true);

    payrollTaxCode_ds.research(true);
}

