public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (payrollTaxCodeDefaultDimension.CategoryID)
    {
        if (ProjCategory::find(payrollTaxCodeDefaultDimension.CategoryID).CategoryType != ProjCategoryType::Cost)
        {
            ret = checkFailed(strFmt("@SYS54897", payrollTaxCodeDefaultDimension.CategoryID));
        }
    }

    return ret;
}

