public void executeQuery()
{
    DictEnum    taxCodeType = new DictEnum(enumName2Id(enumStr(PayrollTaxCodeType)));
    str         currentTaxCodeTypeSymbol = taxCodeType.value2Symbol(payrollTaxCode.Type);

    qbrCurrentTaxCodeTaxGroupId.value(queryValue(currentTaxCodeTypeSymbol));
    qbrCurrentTaxCodeRecId.value(queryValue(PayrollTaxCode.RecId));

    super();
}

public void init()
{
    QueryBuildDataSource    qbdsForGettingSystemDefinedTaxGroup;
    QueryBuildDataSource    qbdsForGettingUserDefinedTaxGroup;
    QueryBuildDataSource    qbdsPayrollTaxGroupCode;
    QueryBuildFieldList     qbflSystemDefinedTaxGroup;
    QueryBuildFieldList     qbflUserDefinedTaxGroup;

    super();

    // Sets up a union query for getting both system defined and user defined tax groups of the currently selected tax code
    this.query().queryType(QueryType::Union);
    qbdsForGettingSystemDefinedTaxGroup = this.query().dataSourceName(this.name());
    qbdsForGettingUserDefinedTaxGroup = this.query().addDataSource(tableNum(PayrollTaxGroup), identifierstr(qbdsForGettingUserDefinedTaxGroup), UnionType::Union);

    // Creates field list for system defined tax group
    qbflSystemDefinedTaxGroup = qbdsForGettingSystemDefinedTaxGroup.fields();
    qbflSystemDefinedTaxGroup.clearFieldList();
    qbflSystemDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, GroupId));
    qbflSystemDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, Description));
    qbflSystemDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, IsEnabledForPostingDefinition));
    qbflSystemDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, IsImmutable));
    qbflSystemDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, RecId));

    // Creates field list for user defined tax group
    qbflUserDefinedTaxGroup = qbdsForGettingUserDefinedTaxGroup.fields();
    qbflUserDefinedTaxGroup.clearFieldList();
    qbflUserDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, GroupId));
    qbflUserDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, Description));
    qbflUserDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, IsEnabledForPostingDefinition));
    qbflUserDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, IsImmutable));
    qbflUserDefinedTaxGroup.addField(fieldNum(PayrollTaxGroup, RecId));

    // Sets up ranges and join relation
    qbrCurrentTaxCodeTaxGroupId = qbdsForGettingSystemDefinedTaxGroup.addRange(fieldNum(PayrollTaxGroup, GroupId));
    qbdsPayrollTaxGroupCode = qbdsForGettingUserDefinedTaxGroup.addDataSource(tableNum(PayrollTaxGroupCode));
    qbdsPayrollTaxGroupCode.relations(true);
    qbdsPayrollTaxGroupCode.joinMode(JoinMode::ExistsJoin);
    qbrCurrentTaxCodeRecId = qbdsPayrollTaxGroupCode.addRange(fieldNum(PayrollTaxGroupCode, TaxCode));

    // Disables filter on fields
    qbrCurrentTaxCodeTaxGroupId.status(RangeStatus::Hidden);
    qbdsForGettingSystemDefinedTaxGroup.addRange(fieldNum(PayrollTaxGroup, Description)).status(RangeStatus::Hidden);
    qbdsForGettingSystemDefinedTaxGroup.addRange(fieldNum(PayrollTaxGroup, IsEnabledForPostingDefinition)).status(RangeStatus::Hidden);
    qbdsForGettingSystemDefinedTaxGroup.addRange(fieldNum(PayrollTaxGroup, IsImmutable)).status(RangeStatus::Hidden);
    qbdsForGettingSystemDefinedTaxGroup.addRange(fieldNum(PayrollTaxGroup, RecId)).status(RangeStatus::Hidden);
}

void clicked()
{
    element.openMaintainTaxCodeVersions();
}

public boolean modified()
{
    boolean ret;
    PayrollTaxCodeDefaultDimension     defaultDimensionReset;

    ret = super();

    if (ret)
    {
        selectedDataAreaRecId = CompanyInfo::findDataArea(legalEntityComboBox.text()).RecId;
        selectedLegalEntity = legalEntityComboBox.text();
        selectedLegalEntityDataAreaId = CompanyInfo::findDataArea(legalEntityComboBox.text()).DataArea;

        if (selectedDataAreaRecId)
        {
            appl.setDefaultCompany(selectedLegalEntityDataAreaId, false);
        }

        element.addLERangeOnTaxCodeDetail();
        payrollTaxCode_ds.research(true);
        element.addLERangeOnTaxAccounting();

        // Handle Default Dimensions
        //find the existing record or set the default values if it doesn't exist
        defaultDimensionReset = PayrollTaxCodeDefaultDimension::findByPayrollTaxCodeLegalEntity(payrollTaxCode.RecId, selectedDataAreaRecId);
        if (!defaultDimensionReset)
        {
            defaultDimensionReset.PayrollTaxCode    = payrollTaxCode.RecId;
            defaultDimensionReset.LegalEntity       = selectedDataAreaRecId;
        }

        // reset the datasource so it knows to do an insert vs. update based on new LE choice
        DimensionEntryControl.parmCompany(legalEntityComboBox.text());
        DimensionEntryControl.reactivate(true);

        LedgerDimension.parmDataAreaId(CompanyInfo::findRecId(selectedDataAreaRecId).DataArea);

        payrollTaxCodeDefaultDimension_ds.executeQuery();
    }

    return ret;
}

public boolean validate()
{
    boolean ret = true;

    if (!CompanyInfo::findDataArea(legalEntityComboBox.text()).RecId)
    {
        ret =  checkFailed(strFmt("@SYS342222", legalEntityComboBox.text()));
    }

    return ret;
}

public void lookup()
{
    ProjCategory::lookupProjCategoryType(this, ProjCategoryType::Cost);
}

public boolean modified()
{
    boolean         ret;
    ProjCategory    projCat;

    ret = super();

    if (ret)
    {
        select firstonly DataAreaId from projCat where projCat.CategoryType == ProjCategoryType::Cost;
        payrollTaxCodeDefaultDimension.CategoryDataAreaId = projCat.DataAreaId;
        selectedDataAreaRecId = CompanyInfo::findDataArea(legalEntityComboBox.text()).RecId;
    }

    return ret;
}

