[Form]
public class PayrollTaxCodeMassUpdate extends FormRun
{
    FormRun                                 callerForm;

    DictEnum                                taxCodeType;
    Query                                   queryTaxCodeForImmutableTaxGroup;
    Query                                   queryTaxCodeForUserDefinedTaxGroup;
    QueryBuildDataSource                    qbdsPayrollTaxCode;
    QueryBuildDataSource                    qbdsPayrollTaxGroupCode;
    QueryBuildRange                         qbrImmutableTaxGroup;
    QueryBuildRange                         qbrUserDefinedTaxGroup;

    VendAccount                             vendAccount;
    ProjCategoryId                          projectCategoryId;
    RefRecId                                selectedDataAreaRecId;
    SelectableDataArea                      selectedLegalEntityDataAreaId;
    DimensionDefault                        dimensionDefaultRecId;

    PayrollTaxCodeMassUpdate                payrollTaxCodeMassUpdate;
    PayrollTaxGroup                         payrollTaxGroup;
    boolean                                 isValidEffectiveDate;
    Timezone                                companyTimeZone;
}

public void init()
{
    utcdatetime defaultDateTime = DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::utcNow(), DateTimeUtil::getCompanyTimeZone());
    companyTimeZone = DateTimeUtil::getCompanyTimeZone();

    if (!element.args().caller() || element.args().dataSet() != tablenum(PayrollTaxGroup) || !this.args().record())
    {
        throw error("@SYS96590");
    }

    if (SysDictClass::isEqualOrSuperClass(classIdGet(element.args().caller()), classNum(FormRun)))
    {
        callerForm = element.args().caller();
    }

    payrollTaxGroup = this.args().record() as PayrollTaxGroup;

    payrollTaxCodeMassUpdate = PayrollTaxCodeMassUpdate::newFromTaxGroup(payrollTaxGroup.GroupId);

    if (!payrollTaxCodeMassUpdate)
    {
        // An instance of the PayrollTaxCodeMassUpdate is required to run the mass update.
        throw error(strfmt("@SYS19384", classstr(PayrollTaxCodeMassUpdate)));
    }

    super();

    //by default effective immediately is selected
    isValidEffectiveDate = true;

    // Set to current date time
    defaultDateTime = DateTimeUtil::newDateTime(DateTimeUtil::date(defaultDateTime), 0);
    effectiveDate.dateTimeValue(defaultDateTime);

    taxCodeType = new DictEnum(enumName2Id(enumStr(PayrollTaxCodeType)));

    legalEntity.text(curext());
    selectedLegalEntityDataAreaId = legalEntity.text();
    selectedDataAreaRecId = CompanyInfo::current();

    ledgerDimension.parmDataAreaId(selectedLegalEntityDataAreaId);

    DimensionEntryControl.parmDisplayValues(true);
    DimensionEntryControl.parmCompany(legalEntity.text());
    DimensionEntryControl.reactivate();
}

/// <summary>
///     Sets fields on the class that are presented to the user on the <c>PayrollTaxCodeMassUpdate</c> form.
/// </summary>
/// <remarks>
///     The <c>PayrollTaxCodeMassUpdate</c> form calls this method to tell the <c>PayrollTaxCodeMassUpdate</c> class what the user has entered.
/// </remarks>
public void initParms()
{
    // checkbox parms
    payrollTaxCodeMassUpdate.parmEditVendorAccount(editVendor.value());
    payrollTaxCodeMassUpdate.parmEditProjectCategory(editProjectCategory.value());
    payrollTaxCodeMassUpdate.parmEditDefaultDimension(editDefaultDimension.value());
    payrollTaxCodeMassUpdate.parmEditMainAccount(editLedgerDimension.value());
    payrollTaxCodeMassUpdate.parmEditW2BoxNumber(editW2BoxNumber.value());
    payrollTaxCodeMassUpdate.parmEditW2Box20Label(editW2Box20Label.value());

    //value parms
    dimensionDefaultRecId = DimensionEntryControl.saveAttributeValueSet();

    payrollTaxCodeMassUpdate.parmLegalEntityRecId(selectedDataAreaRecId);
    payrollTaxCodeMassUpdate.parmVendorAccount(Vendor.text());
    payrollTaxCodeMassUpdate.parmProjCategoryId(projectCategory.text());
    payrollTaxCodeMassUpdate.parmDimensionDefaultRecId(dimensionDefaultRecId);
    payrollTaxCodeMassUpdate.parmMainAccount(payrollTmpTaxAccountingRule.LedgerDimension);
    payrollTaxCodeMassUpdate.parmEditW2BoxNumber(editW2BoxNumber.value());
    payrollTaxCodeMassUpdate.parmW2BoxNumber(W2BoxNumber.selection());
    payrollTaxCodeMassUpdate.parmW2BoxLabel(W2BoxLabel.valueStr());
    payrollTaxCodeMassUpdate.parmEditW2Box20Label(editW2Box20Label.value());
    payrollTaxCodeMassUpdate.parmW2Box20Label(W2Box20Label.selection());
    payrollTaxCodeMassUpdate.parmW2Box20Other(W2Box20Other.valueStr());

    if (asOfDateGroup.optionValue() == 1)
    {
        payrollTaxCodeMassUpdate.parmEffectiveOnDate(DateTimeUtil::date(DateTimeUtil::removeTimeZoneOffset(effectiveDate.dateTimeValue(), companyTimeZone)));
    }
    else
    {
        payrollTaxCodeMassUpdate.parmEffectiveOnDate(DateTimeUtil::date(DateTimeUtil::utcNow()));
    }

    if (payrollTaxCodeMassUpdate.parmVendorAccount())
    {
        payrollTaxCodeMassUpdate.parmVendDataAreaId(selectedLegalEntityDataAreaId);
    }
    else
    {
        payrollTaxCodeMassUpdate.parmVendDataAreaId('');
    }

    if (payrollTaxCodeMassUpdate.parmProjCategoryId())
    {
        payrollTaxCodeMassUpdate.parmCategoryDataAreaId(selectedLegalEntityDataAreaId);
    }
    else
    {
        payrollTaxCodeMassUpdate.parmCategoryDataAreaId('');
    }
}

public void legalEntityChanged()
{
    editVendor.value(false);
    editprojectCategory.value(false);
    editDefaultDimension.value(false);
    editLedgerDimension.value(false);
    editW2BoxNumber.value(false);
    editW2Box20Label.value(false);
    NowGroup.optionValue(1);

    editVendor.clicked();
    editprojectCategory.clicked();
    editDefaultDimension.clicked();
    editLedgerDimension.clicked();
    NowGroup.clicked();
    editW2BoxNumber.clicked();
    editW2Box20Label.clicked();
}

public void run()
{
    super();

    this.wait(true);
}

public void updateOkButton()
{
    boolean isW2BoxNumberValid = true;
    boolean isW2Box20LabelValid = true;

    if (editW2BoxNumber.value() && (W2BoxNumber.selection() == PayrollW2BoxNumber::NotSelected))
    {
        isW2BoxNumberValid = false;
    }

    if (editW2Box20Label.value() && (W2Box20Label.selection() == PayrollW2Box20Label::NotSelected))
    {
        isW2Box20LabelValid = false;
    }

    if ((editVendor.value() || editProjectCategory.value() || editDefaultDimension.value() || editLedgerDimension.value() ||
        editW2BoxNumber.value() || editW2Box20Label.value()) && isW2BoxNumberValid && isW2Box20LabelValid && isValidEffectiveDate)
    {
        OkButton.enabled(true);
    }
    else
    {
        OkButton.enabled(false);
    }
}

/// <summary>
///     Determines whether he <c>asOfDateGroup</c> has been selected.
/// </summary>
/// <returns>
///     true if the <c>asOfDateGroup</c> has been selected; otherwise, false.
/// </returns>
private boolean isAsOfDateSelected()
{
    return (asOfDateGroup.optionValue() == 1);
}

/// <summary>
///     Determines whether he <c>nowGroup</c> has been selected.
/// </summary>
/// <returns>
///     true if the <c>nowGroup</c> has been selected; otherwise, false.
/// </returns>
private boolean isNowSelected()
{
    return (nowGroup.optionValue() == 1);
}

public boolean validateEffectiveDate()
{
    utcdatetime dateTimeNow = DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::utcNow(), DateTimeUtil::getCompanyTimeZone());
    boolean     isValid = true;

    dateTimeNow = DateTimeUtil::newDateTime(DateTimeUtil::date(dateTimeNow), 0);

    if (element.isAsOfDateSelected() && (effectiveDate.dateTimeValue() < dateTimeNow))
    {
        error("@Payroll:TheEffectiveDateAndTimeMustBeLaterThanTheCurrentDateAndTimeE");
        effectiveDate.dateTimeValue(dateTimeNow);
    }

    return isValid;
}

public void init()
{
    super();

    //
    //Create query for tax codes for Immutable tax group
    //
    queryTaxCodeForImmutableTaxGroup = new Query();
    qbdsPayrollTaxCode = queryTaxCodeForImmutableTaxGroup.addDataSource(tableNum(PayrollTaxCode));
    qbrImmutableTaxGroup = qbdsPayrollTaxCode.addRange(fieldNum(PayrollTaxCode, Type));

    //
    //Create query for tax codes for user defined tax group
    //
    queryTaxCodeForUserDefinedTaxGroup = new Query();
    qbdsPayrollTaxCode = queryTaxCodeForUserDefinedTaxGroup.addDataSource(tableNum(PayrollTaxCode));
    qbdsPayrollTaxGroupCode = qbdsPayrollTaxCode.addDataSource(tableNum(PayrollTaxGroupCode));
    qbdsPayrollTaxGroupCode.addLink(fieldNum(PayrollTaxCode,RecId),fieldNum(PayrollTaxGroupCode,TaxCode));
    qbdsPayrollTaxGroupCode.joinMode(JoinMode::ExistsJoin);
    qbrUserDefinedTaxGroup = qbdsPayrollTaxGroupCode.addRange(fieldNum(PayrollTaxGroupCode,TaxGroup));
}

/// <summary>
/// Overrides the datasource query with respect to the tax group type
/// </summary>
public void executeQuery()
{
    int taxCodeTypeId = taxCodeType.symbol2Value(payrollTaxGroup.GroupId);

    if (payrollTaxGroup.IsImmutable == NoYes::Yes)
    {
        qbrImmutableTaxGroup.value(queryValue(taxCodeTypeId));
        this.query().clearBaseQueries();
        this.query(queryTaxCodeForImmutableTaxGroup);
    }
    else
    {
        qbrUserDefinedTaxGroup.value(queryValue(payrollTaxGroup.RecId));
        this.query().clearBaseQueries();
        this.query(queryTaxCodeForUserDefinedTaxGroup);
    }

    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        selectedDataAreaRecId = CompanyInfo::findDataArea(legalEntity.text()).RecId;
        selectedLegalEntityDataAreaId = CompanyInfo::findRecId(selectedDataAreaRecId).DataArea;

        if (selectedDataAreaRecId)
        {
            appl.setDefaultCompany(selectedLegalEntityDataAreaId, false);
        }

        DimensionEntryControl.parmCompany(legalEntity.text());
        DimensionEntryControl.reactivate(true);

        ledgerDimension.parmDataAreaId(selectedLegalEntityDataAreaId);

        element.legalEntityChanged();
    }

    return ret;
}

public boolean validate()
{
    boolean isLegalEntityValid = true;

    if (!CompanyInfo::findDataArea(legalEntity.text()).RecId)
    {
        isLegalEntityValid = checkFailed(strFmt("@SYS342222",legalEntity.text()));
    }

    return isLegalEntityValid;
}

