public void lookup()
{
    VendTable::lookupVendor(this, selectedLegalEntityDataAreaId);
}

public boolean validate()
{
    boolean isVendorValid;

    isVendorValid = super();

    if (isVendorValid)
    {
        if (!VendTable::findByDataAreaId(Vendor.text(),selectedLegalEntityDataAreaId).RecId)
        {
            isVendorValid = checkFailed(strFmt("@Payroll:TheValue1InField2IsNotFoundInTheRelatedTable3", Vendor.text(), "@SYS8079", tableId2pname(tableNum(VendTable))));
        }
    }

    return isVendorValid;
}

