public int selectionChange()
{
    int ret;

    ret = super();

    if (W2Box20Label.selection() == PayrollW2Box20Label::Other)
    {
        W2Box20Other.enabled(true);
    }
    else
    {
        W2Box20Other.enabled(false);

        // If W2Box20Other is not empty then only clear the field
        if (W2Box20Other.valueStr() != '')
        {
            W2Box20Other.text('');
        }
    }

    //enable/disable update button
    element.updateOkButton();

    return ret;
}

void clicked()
