public void init()
{
    Args args = element.args();

    if (!args || !element.isArgsValid(args))
    {
        throw error("@PRO467");
    }

    pdsFormCtrl = PdsFormCtrl_PdsApprovedVendorList::newFromMenuItem(args.menuItemName(), element);

    pdsFormCtrl.initPre();

    super();
    dateEffectivenessFormController = DateEffectivenessFormController::newFormControls(PdsApprovedVendorList_ds,
                                                                            null,
                                                                            ButtonViewAll,
                                                                            ButtonViewCurrent,
                                                                            RemoveRecord,
                                                                            EditRecord);

    dateEffectivenessFormController.setViewState(DateEffectivenessViewState::All);

    pdsFormCtrl.initPost();

    // we use default button for edit
    EditRecord.visible(false);

    ViewEditMode viewModeForForm = pdsFormCtrl.parmAllowEdit() ? ViewEditMode::Edit : ViewEditMode::View;
    PdsApprovedVendorList_ds.formRun().design().ViewEditMode(viewModeForForm);
}

/// <summary>
/// Checks if the passed args is valid.
/// </summary>
/// <param name = "_args">
/// The passed args.
/// </param>
/// <returns>
/// true if the args is valid; otherwise, false.
/// </returns>
protected boolean isArgsValid(Args _args)
{
    return !_args.record()
            || _args.record().TableId == tablenum(InventTable)
            || _args.record().TableId == tablenum(VendTable)
            || _args.record().TableId == tablenum(PurchTable)
            || _args.record().TableId == tablenum(PurchProductApprovedVendorStaging);
}

