[Form]
    /// <summary>
    /// Form to request a decision about the resolution of a same lot batch reservation issue.
    /// </summary>
    /// <remarks>
    /// The form will need the following parameters set through the parm* methods:
    /// movement:       the movement to reserve for
    /// reserveNow:     the quantity to reserve (if the item on the movement
    ///                 is a non-catch weight item)
    /// cwReserveNow:   the catch weight quantity to reserve (if the item on
    ///                 the movement is a catch weight item
    /// inventDimReq:   the inventory dimension that the reservation request
    ///                 originally was for
    /// error:          the error that occured when the reservation was
    ///                 attempted
    /// Once the form closes, it will contain a resolution object to resolve
    /// the issue. The object can be obtained through the sameLotResolution method.
    /// </remarks>
    /// <example>
    /// This example is copied from the InventMovement class:
    /// public PdsSameLotBatchResolution pdsAskSameLotResolution(
    ///     PdsSameLotError _error,
    ///     InventDim       _inventDimReq,
    ///     InventQty       _reserveNow,
    ///     PdsCWInventQty  _cwReserveNow = 0)
    /// {
    ///     Args    args = new Args(formstr(PdsAskSameLotForm));
    ///     Object  formRun;
    ///
    ///     formRun = classFactory.formRunClass(args);
    ///     formRun.init();
    ///     formRun.parmMovement(this);
    ///     formRun.parmInventDimReq(_inventDimReq);
    ///     formRun.parmError(_error);
    ///     formRun.parmPdsCWReserveNow(_cwReserveNow);
    ///     formRun.parmReserveNow(_reserveNow);
    ///     formRun.initPost();
    ///
    ///     formRun.run();
    ///     formRun.wait();
    ///
    ///     return formRun.sameLotResolution();
    /// }
    /// </example>
    public class PdsAskSameLotForm extends FormRun
    {
        PdsSameLotError             error;
        PdsSameLotBatchResolution   resolution;
        InventMovement              movement;

        PdsCWInventQty              cwReserveNow;
        InventQty                   reserveNow;

        PdsCWInventQtySameLotUpdate cwInventUpdQty;
        InventQtySameLotUpdate      inventUpdQty;

        [FormObservable]
        boolean                     cwItem;
        boolean                     reserveOnOrdered;

        InventAvailabilityByUnit    availabilityOnRequestedDims;
        InventAvailabilityByUnit    availabilitySuggestedBatch;
        InventAvailabilityByUnit    availabilityMax;
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = resolution != null;
    }

    return ret;
}

protected real displaySign(real _amount)
{
    real displaySign = sign(element.reserveTransQty());
    if (sign(_amount) != displaySign)
    {
        return _amount * -1;
    }
    else
    {
        return _amount;
    }
}

    public void init()
    {
        resolution = null;

        super();

        reserveOnOrdered = InventParameters::find().ReserveOnOrdered;

        configurationRaw.visible(false);
        sizeRaw.visible(false);
        colorRaw.visible(false);
		versionRaw.visible(false);
        styleRaw.visible(false);
    }

public void initInventSumBatch()
{
    InventDimParm   inventDimParm;
    InventAvailabilityByUnitWithDim firstOrLargestBatchAvailability;

    if (inventDimReq)
    {
        inventDimParm.initFromInventDim(inventDimReq);
        inventDimParm.ItemIdFlag = NoYes::Yes;

        if (inventDimParm.InventBatchIdFlag)
        {
            inventDimBatch.data(movement.inventdim());
            inventDimBatch.InventBatchId = inventDimReq.InventBatchId;
            inventDimParm.clear();
            inventDimParm.initFromInventDim(inventDimBatch);
            inventDimParm.ItemIdFlag = NoYes::Yes;

            availabilitySuggestedBatch = InventAvailabilityProvider::findByDimWithAutoReservationCriteria(movement,
                                                                                                          inventDimBatch,
                                                                                                          inventDimParm);
        }
        else
        {
            inventDimParm.initFromInventDim(movement.inventdim());
            firstOrLargestBatchAvailability = InventAvailabilityProvider::findSameBatchFirstOrLargest(
                                                        movement,
                                                        movement.inventdim(),
                                                        inventDimParm,
                                                        movement.transQty() * movement.transSign(),
                                                        movement.pdsCWTransQty() * movement.transSign());

            availabilitySuggestedBatch = firstOrLargestBatchAvailability.availabilityByUnit();
            inventDimBatch.data(movement.inventdim());
            inventDimBatch.InventBatchId = firstOrLargestBatchAvailability.inventDim().inventBatchId;
        }

        if (cwItem)
        {
            cwInventUpdQty = availabilitySuggestedBatch.parmPdsCWInventAvailability().pdsCWAvailReservation(reserveOnOrdered);
            inventUpdQty = PdsCatchWeight::inventQty(
                        movement.itemId(),
                        cwInventUpdQty,
                        movement.transUnitId());
        }
        else
        {
            inventUpdQty = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(movement.itemId(),
                                                                                                 movement.inventDimId(),
                                                                                                 availabilitySuggestedBatch.parmInventAvailability().availReservation(reserveOnOrdered),
                                                                                                 movement.inventTable().inventUnitId(),
                                                                                                 movement.transUnitId(),
                                                                                                 NoYes::Yes);
        }
    }
}

/// <summary>
///     Updates the transaction associated with the order with a dimension that has selected the new created batch by this dialog.
/// </summary>
public void initInventSumMax()
{
    InventDimParm   inventDimParm;
    InventDim       inventDim;

    cwItem = PdsGlobal::pdsIsCWItem(movement.itemId());
    inventUpdQty = movement.transQtyUnit() * movement.transSign();
    cwInventUpdQty = cwItem ? movement.pdsCWTransQty() * movement.transSign() : 0;

    inventDimParm.initPhysicalDimensionsFromInventDim(movement.inventDimGroupSetup(),movement.inventdim());
    inventDimParm.ItemIdFlag = NoYes::Yes;
    inventDim.data(movement.inventdim());
    inventDim.clearNotSelectedDim(inventDimParm);

    availabilityMax = InventAvailabilityProvider::findByDimWithAutoReservationCriteria(movement, inventDim, inventDimParm);

    inventDimMax.data(inventDim);
    inventDimMax.InventBatchId = '';

    this.updateInventDimDisplay();
}

public void initInventSumReq()
{
    if (inventDimReq)
    {
        availabilityOnRequestedDims = InventAvailabilityProvider::findByDimWithAutoReservationCriteria(movement, inventDimReq);
    }
}

void initPost()
{
    if (!movement)
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    movement.initForAutoReservation();

    this.initInventSumMax();
    this.initInventSumReq();
    this.initInventSumBatch();
}

