public PdsSameLotError parmError(PdsSameLotError _error = error)
{
    if (!prmisDefault(_error))
    {
        error = _error;
    }

    return error;
}

public InventDim parmInventDimReq(InventDim _inventDimReq = inventDimReq)
{
    if (!prmisDefault(_inventDimReq))
    {
        inventDimReq.data(_inventDimReq);

        // When the dimension comes from a summarized inventory query, any dimension fields not in the
        // group by will be marked with "unretrieved" in the UI; clearing them clears this mark.
        inventDimReq.clearNotSelectedDim(inventDimReq.toDimParm());
    }

    return inventDimReq;
}

public InventMovement parmMovement(InventMovement _movement = null)
{
    if (!prmisDefault(_movement))
    {
        movement = _movement;
    }

    return movement;
}

