public display PdsCWInventQty pdsCWReserveMax()
{
    return movement.transIdSum().pdsCWOnOrder() * movement.transSign();
}

public display PdsCWInventQty pdsCWReserveNow()
{
    return element.displaySign(cwReserveNow);
}

public display PdsCWInventQty pdsCWReserveTransQty()
{
    return movement.pdsCWTransQty() * movement.transSign();
}

