    return unitId;
}

public edit PdsCWInventQtySameLotUpdate pdsCWUpdQty(
    boolean                     _set,
    PdsCWInventQtySameLotUpdate _cwUpdQty = cwInventUpdQty)
{
    if (_set)
    {
        cwInventUpdQty = _cwUpdQty;
        inventUpdQty = PdsCatchWeight::inventQty(
                        movement.itemId(),
                        cwInventUpdQty,
                        movement.transUnitId());
    }

    return cwInventUpdQty;
}

public display InventQty reserveMax()
{
    InventQty   qty;

    if (cwItem)
    {
        qty = PdsCatchWeight::inventQty(
                    movement.itemId(),
                    this.pdsCWReserveMax(),
                    movement.transUnitId());
    }
    else
    {
        qty = this.toUnitConvert(movement.transIdSum().onOrder() * movement.transSign());
    }

    return qty;
}

public display InventQty reserveNow()
{
    return element.displaySign(this.toUnitConvert(reserveNow));
}

public display InventQty reserveTransQty()
{
    return this.toUnitConvert(movement.transQtyUnit() * movement.transSign());
}

public void run()
{
    this.initPost();

    super();
}

public PdsSameLotBatchResolution sameLotResolution()
