public void updateInventDimDisplay()
{
    List                dimFields  = InventDim::dimFieldsList();
    InventDimGroupSetup inventDimGroupSetup;
    ListEnumerator      enumerator = dimFields.getEnumerator();
    FieldId             inventDimFieldId;
    boolean             enabled;
    Object              fc;

    if (movement)
    {
        inventDimGroupSetup = InventDimGroupSetup::newInventTable(movement.inventTable());

        while (enumerator.moveNext())
        {
            inventDimFieldId = enumerator.current();

            enabled = inventDimGroupSetup.getFieldSetup(inventDimFieldId).isActive();
            fc = inventDimReq_DS.object(inventDimFieldId);
            fc.visible(enabled);
            fc = inventDimBatch_DS.object(inventDimFieldId);
            fc.visible(enabled);
            fc = inventDimMax_DS.object(inventDimFieldId);
            fc.visible(enabled);
        }
        updQty.enabled(!cwItem
            && error != PdsSameLotError::InvalidBatch);
        updQty.skip(cwItem
            || error == PdsSameLotError::InvalidBatch);
        pdsCWUpdQty.enabled(cwItem
            && error != PdsSameLotError::InvalidBatch);
        pdsCWUpdQty.skip(!cwItem
            || error == PdsSameLotError::InvalidBatch);
    }
}

public edit InventQtySameLotUpdate updQty(boolean _set, InventQtySameLotUpdate _updQty = inventUpdQty)
{
    if (_set)
    {
        inventUpdQty = _updQty;
    }

    return inventUpdQty;
}

    [Hookable, Wrappable]
    private void updateProductDimensionGroupsVisibility(InventDim _inventDim)
    {
        #InventDimDevelop

        if (_inventDim.configId)
        {
            configurationRaw.visible(true);
        }
        if (_inventDim.InventSizeId)
        {
            sizeRaw.visible(true);
        }
        if (_inventDim.InventColorId)
        {
            colorRaw.visible(true);
        }
		if (_inventDim.InventVersionId)
        {
            versionRaw.visible(true);
        }
        if (_inventDim.InventStyleId)
        {
            styleRaw.visible(true);
        }
    }

