public InventQty convertQtyBetweenUnits(
    InventQty           _value,
    UnitOfMeasureSymbol _fromUnitId = unitIdInvent,
    UnitOfMeasureSymbol _toUnitId = unitIdSecondary)
{
    if (_fromUnitId == _toUnitId)
    {
        return _value;
    }

    this.setupUnitConversion();

    if (unitIdInvent == _fromUnitId && unitIdSecondary == _toUnitId)
    {
        return unitIdInventConverter.convertValue(_value);
    }

    return unitIdSecondaryConverter.convertValue(_value);
}

void  init()
{
    if (!element.args().record()
        && element.args().record().TableId != tableNum(InventTable))
    {
        throw error("@PDS1331");
    }
    else
    {
        inventTable = element.args().record();
    }

    super();

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

