public void refreshSelection()
{
    InventDim               tmpInventDim;
    InventBatch             inventBatch;
    InventOnHandQty         inventOnHandQty;
    InventDimParm           inventDimParm;
    TmpPdsBatchSelect       batchSelect;
    TmpPdsBatchAttribSelect tmpAttrSel;

    #define.noCust('')

    batchSelect.setTmpData(tmpPdsBatchSelect);
    delete_from batchSelect;

    if (tmpPdsBatchAttribSelect.RecId == 0)
    {
        tmpAttrSel.setTmpData(tmpPdsBatchAttribSelect);

        tmpAttrSel = InventTable::pdsFillDefaultBatchAttribforItem(
                        inventTable.ItemId);

        element.setAttribSelect(tmpAttrSel);
        tmpPdsBatchAttribSelect_ds.research();
    }

    inventDimParm = InventDimParm::activeDimFlag(InventDimGroupSetup::newItemId(inventTable.ItemId));

    InventBatch inventBatchOtherItem;
    select firstonly RecId from inventBatch
        where inventBatch.itemId == inventTable.ItemId
        exists join inventBatchOtherItem
            where  inventBatchOtherItem.itemId        != inventBatch.itemId
                && inventBatchOtherItem.inventBatchId == inventBatch.inventBatchId;

    if (inventBatch.RecId)
    {
        InventSum inventSumExist;
        select tmpInventDim
            exists join inventBatch
                where  inventBatch.InventBatchId == tmpInventDim.InventBatchId
                    && inventBatch.ItemId == inventTable.ItemId
            exists join inventSumExist
            where  inventSumExist.ClosedQty   == NoYes::No
                && inventSumExist.ItemId      == inventBatch.itemId
                && inventSumExist.inventDimId == tmpInventDim.inventDimId;
    }
    else
    {
        select tmpInventDim
            exists join inventBatch
                where  inventBatch.InventBatchId    == tmpInventDim.InventBatchId
                    && inventBatch.ItemId           == inventTable.ItemId;
    }

    while (tmpInventDim)
    {
        select firstonly RecId
            from batchSelect
            where batchSelect.InventBatchId == tmpInventDim.InventBatchId
               && batchSelect.ItemId == inventTable.ItemId;
        if (!batchSelect.RecId)
        {
            inventOnHandQty = InventOnHandQty::newParameters(
                                inventTable.ItemId,
                                tmpInventDim,
                                inventDimParm);

            if (inventOnHandQty.availPhysical() > 0)
            {
                if (PdsBatchAttributes::pdsCheckBatchAtribReserve(
                        inventTable.ItemId,
                        #noCust,
                        tmpInventDim.InventBatchId,
                        tmpPdsBatchAttribSelect))
                {
                    batchSelect.clear();
                    batchSelect.ItemId = inventTable.ItemId;
                    batchSelect.InventBatchId = tmpInventDim.InventBatchId;
                    batchSelect.insert();
                }
            }
        }

        next tmpInventDim;
    }

    tmpPdsBatchSelect_ds.research();
}

