/// <summary>
/// Populate form data source with provided record(s)
/// </summary>
/// <param name="_attrSel">
/// Recordset with which to populate
/// </param>
/// <remarks>
/// Allows to setting of the form data sources with custom recordset
/// </remarks>
void setAttribSelect(TmpPdsBatchAttribSelect _attrSel)
{
    TmpPdsBatchAttribSelect     tmpAttrSel;

    tmpAttrSel.setTmpData(tmpPdsBatchAttribSelect);
    delete_from tmpAttrSel;

    while select _attrSel
    {
        tmpAttrSel.clear();
        tmpAttrSel.data(_attrSel);
        tmpAttrSel.insert();
    }
}

private void setupUnitConversion()
{
    if (!itemIdUnits
        || inventSum.ItemId != itemIdUnits)
    {
        itemIdUnits     = inventSum.ItemId;
        unitIdInvent    = inventSum.inventTable().inventUnitId();

        if (!EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                          inventSum.InventDimId,
                                                                                          unitIdInvent,
                                                                                          unitIdSecondary))
        {
            unitIdSecondary = unitIdInvent;
        }

        unitIdInventConverter = EcoResProductUnitConverter::newGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                                  inventSum.InventDimId,
                                                                                                  unitIdInvent,
                                                                                                  unitIdSecondary,
                                                                                                  NoYes::Yes);

        unitIdSecondaryConverter = EcoResProductUnitConverter::newGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                                     inventSum.InventDimId,
                                                                                                     unitIdSecondary,
                                                                                                     unitIdInvent,
                                                                                                     NoYes::Yes);
    }
}

edit UnitOfMeasureSymbol unitId(boolean set, UnitOfMeasureSymbol unitID)
{
    if (set)
    {
        unitIdSecondary = unitID;
    }
    return unitIdSecondary;
}

