void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm       inventDimParm;
    boolean             isCWItem = PdsGlobal::pdsIsCWItem(inventTable.ItemId);

    inventDimParm = InventDimParm::activeDimFlag(InventDimGroupSetup::newItemId(inventTable.ItemId));
    inventDimParm.ItemIdFlag = NoYes::No;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            reserveOnOrdered = InventParameters::find().ReserveOnOrdered;
            element.inventDimSetupObject().initFromCaller();
            element.inventDimSetupObject().parmCtrlItemId(itemId);
            element.inventDimSetupObject().parmDimParmEnabled(inventDimParm);
            element.inventDimSetupObject().parmDimParmVisibleGrid(
                inventDimParm,
                true);
            element.inventDimSetupObject().dimFieldsActive(inventDimParm);
            element.inventDimSetupObject().formSetControls(true);
            if (element.inventDimSetupObject().parmDimParmVisibleGrid()
                        .ItemIdFlag == NoYes::No)
            {
                    itemName.visible(false);
            }

            nonCWInventory.visible(!isCWItem);
            cwInventory.visible(isCWItem);

            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Returns Catch Weight qty available for reservation after removing
/// Not nettable or expired Batch if Shelf Life item.
/// </summary>
/// <param name="_inventSum">Current InventSum record</param>
/// <returns>Available Catch Weight Qty for
/// reservation</returns>
public display PdsCWAvailReservation pdsCWAvailReservation(InventSum _inventSum)
{
    PdsCWAvailReservation   availReserve;

    availReserve = _inventSum.pdsCWAvailReservation(reserveOnOrdered);

    if (#PdsShelfEnabled)
    {
        availReserve -= cwNonNetQty;
        availReserve -= cwExpQty;
    }

    return availReserve;
}

