/// <summary>
/// Returns the CW availabale physical qty.
/// </summary>
/// <param name="_inventSum">Current InventSum record</param>
/// <returns>CW available physical qty.</returns>
public display PdsCWAvailPhysical pdsCWAvailPhysical(InventSum _inventSum)
{
    PdsCWAvailPhysical  availPhysical;

    availPhysical = _inventSum.pdsCWAvailPhysicalCalculated();

    if (#PdsShelfEnabled)
    {
        availPhysical -= cwNonNetQty;
        availPhysical -= cwExpQty;
    }

    return availPhysical;
}

/// <summary>
/// Returns the catch weight physical quantity available including
/// ordered minus ordered reservation.
