[Form]
public class PdsInventBatchBestBefore extends FormRun
{
    PdsInventDimCtrlFormBestBefore      inventDimFormSetup;

    ItemId                              currentItemId;
    InventCostPriceCache                inventCostPriceCache;

    QueryBuildRange                     criteriaDate;
    QueryBuildRange                     criteriaOnHand;

    boolean                             doSysLastValue;
    NoYes                               reserveOnOrdered;

    [FormObservable]
    PdsInventQty                        nonNetQty;
    [FormObservable]
    PdsCWInventQty                      cwNonNetQty;
    [FormObservable]
    PdsInventQty                        expQty;
    [FormObservable]
    PdsCWInventQty                      cwExpQty;

    InventAvailabilityFormCache         inventAvailabilityFormCache;

    #Define.CurrentVersion(1)
    #Localmacro.CurrentList
        lastDate
    #endMacro

}

public void activate(boolean _active)
{
    element.pdsShow();

    super(_active);
}

public void addBasicRanges()
{
    QueryBuildDataSource    queryBuildDataSourceInventBatch     = inventBatch_DS.queryBuildDataSource();
    QueryBuildDataSource    queryBuildDataSourceInventSum       = inventSum_DS.queryBuildDataSource();

    criteriaDate       = queryBuildDataSourceInventBatch.addRange(fieldNum(InventBatch, PdsBestBeforeDate));
    criteriaOnHand     = queryBuildDataSourceInventSum.addRange(fieldNum(InventSum,     ClosedQty));

    // by the form, the sum is only joined to the dim, so it should link to the item
    queryBuildDataSourceInventSum.addLink(
        fieldNum(InventBatch, ItemId),
        fieldNum(InventSum, ItemId),
        inventBatch_DS.name());

    criteriaDate.value(strFmt('((%1) && (%1 <= %2))',
            fieldStr(InventBatch, PdsBestBeforeDate),
            date2StrXpp(ctrlDate.dateValue())));

    criteriaOnHand.value(queryValue(NoYes::No));
}

public void close()
{
    xSysLastValue::saveLast(this);

    super();
}

public void doRefresh()
{
    element.doResearch();
}

public void hideQualityManagement()
{
    qualityOrderStatusDisplay.visible(false);
    inquiries.visible(false); // curretly only quality related queries)
    inventNonConformanceTable.visible(false);
    inventQualityOrderTable.visible(false);
}

public void init()
{
    reserveOnOrdered = NoYes::No;

    super();

    doSysLastValue = element.args().caller() ? false : true;

    if (doSysLastValue)
    {
        xSysLastValue::getLast(this);
    }

    if (!ctrlDate.dateValue())
    {
        ctrlDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    }

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

    element.addBasicRanges();
    element.updateDesign(InventDimFormDesignUpdate::Init);
}

