{
}

public PdsInventDimCtrlFormBestBefore inventDimSetupObject()
{
    if (!inventDimFormSetup)
    {
        inventDimFormSetup  = PdsInventDimCtrlFormBestBefore::newFromForm(element);
        inventDimFormSetup.parmLargeRightClickForm(NoYes::No);
    }

    return inventDimFormSetup;
}

