public boolean unpack(container _packedClass)
{
    Integer                 version     = conPeek(_packedClass, 1);
    TransDate               lastDate;

    switch (version)
    {
        case #CurrentVersion :
            [version,#CurrentList] = _packedClass;
            break;
        default :
            return false;
    }

    if (lastDate)
    {
        ctrlDate.dateValue(lastDate);
    }
    else
    {
        ctrlDate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    }

    return true;
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
            reserveOnOrdered = InventParameters::find().ReserveOnOrdered;
        // No break
        case InventDimFormDesignUpdate::LinkActive    :
            element.inventDimSetupObject().initFromCaller();
            element.inventDimSetupObject().parmDimParmVisible(element.inventDimSetupObject().parmDimParmVisibleGrid());
        // No break
        case InventDimFormDesignUpdate::Active        :
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true, true);
            break;

        default :
            throw error(strFmt("@SYS54195", funcName()));
    }
}

public void executeQuery()
{
    element.inventDimSetupObject().modifyQueryWithBatch(inventSum_DS,inventDim_DS,inventBatch_ds);

    inventAvailabilityFormCache.refreshCache();

    super();
}

