public void pdsNonNetExpiredBatch(InventSum _inventSum = inventSum)
{
    if (#PdsEnabled)
    {
        [nonNetQty, cwNonNetQty] = inventSum.pdsNonNetPhysical(
                                        _inventSum.joinParent(),
                                        inventBatch);
    }

    if (#PdsShelfEnabled
        && InventTable::find(_inventSum.ItemId).isShelfLifeItem())
    {
        [expQty,cwExpQty]        = inventSum.pdsExpiredPhysical(
                                        _inventSum.joinParent(),
                                        inventBatch);
    }
}

int active()
{
    int ret;

    if (#PdsEnabled)
    {
        element.resetDispShelfQty();
        this.pdsNonNetExpiredBatch(inventSum);
    }

    ret = super();

    inventDim_ds.active();

    return ret;
}

void init()
{
    super();

    element.inventDimSetupObject().initQueryInventSum(inventSum_DS);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (!this.dateValue())
        {
            ret = checkFailed("@SYS17635");
        }

        criteriaDate.value(strFmt('((%1) && (%1 <= %2))',
            fieldStr(InventBatch, PdsBestBeforeDate),
            date2StrXpp(ctrlDate.dateValue())));

        inventBatch_ds.executeQuery();
    }

    return ret;
}
