public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    // Remove auto-added data entities from the menu.
    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();
        if (dataEntityOptions.dataEntityName() == tableStr(TAMTradeAllowanceAgreementMerchandisingEventBillBackEntity))
        {
            dataEntityOptionsIterator.delete();
            break;
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(SalesRebateAgreementHeaderV2Entity), ExportSalesRebateAgreementCustom);
    exportOption.displayName("@SCM:CustomerRebateAgreementHeaderLines");
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportSalesRebateAgreementCustom:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(SalesRebateAgreementHeaderV2Entity));

            var headerEntityInfo = context.findEntityByName(tableStr(SalesRebateAgreementHeaderV2Entity));

            var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(SalesRebateAgreementHeaderV2Entity));
            var headerAgreementIdFilter = headerFilterBuilder.areEqual(fieldStr(SalesRebateAgreementHeaderV2Entity, RebateAgreementId), PdsRebateAgreement.RebateAgreementId);
            headerEntityInfo.filter(headerFilterBuilder.and(headerFilterBuilder.companyFilter(), headerAgreementIdFilter));

            element.addContext(context,
                               tableStr(SalesRebateAgreementLineEntity),
                               fieldStr(SalesRebateAgreementLineEntity, RebateAgreementId),
                               PdsRebateAgreement.RebateAgreementId);
            break;
    }
    return context;
}

private void addContext(ExportToExcelDataEntityContext _context,
    TableName            _entityName,
    FieldName            _filterField,
    PdsRebateAgreementId _filterValueAgreementId)
{
    var entityInfo = _context.addEntityDefault(_entityName);
    var filterBuilder = new ExportToExcelFilterTreeBuilder(_entityName);
    var agreementIdFilter = filterBuilder.areEqual(_filterField, _filterValueAgreementId);
    entityInfo.filter(filterBuilder.and(filterBuilder.companyFilter(), agreementIdFilter));
}

public boolean canActivateApprovalWorkflow(TableId tableId, RecId recId)
{
    WorkflowVersionTable    versionTable = WorkFlowTable::findActiveTemplate(workFlowTypeStr(MCRCustRebateAppr)).getActiveVersion();
    SysDictWorkflowType     sysDictWorkflowTemplate = new SysDictWorkflowType(workFlowTypeStr(MCRCustRebateAppr));

    if (versionTable.ActivationConditionType == NoYes::Yes
        && sysDictWorkflowTemplate.disableActivationConditions() == false)
    {
        return Workflow::evaluateWorkflowActivationCondition(versionTable, recId, tableId);
    }

    //if there is not activation condition set up, allow activation by default
    return true;
}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = super();

    if (ret && pdsRebateAgreement.RecId)
    {
        ret = element.canActivateApprovalWorkflow(tableNum(PdsRebateAgreement), PDSRebateAgreement.RecId);
    }

    return ret;
}

