public void initRecord()
{
    boolean custRebateCodeAllFlag = pdsRebateAgreement.PdsCustRebateCode == TableGroupAll::All;
    boolean itemRebateCodeAllOrCustomFlag = (pdsRebateAgreement.PdsItemRebateCode == PdsRebateItemCode::All
                                                || pdsRebateAgreement.PdsItemRebateCode == PdsRebateItemCode::Selection);
    boolean cumulateSalesByCustPeriodFlag = pdsRebateAgreement.PdsCumulateSalesBy == PdsCumulationPeriod::CustomizedPeriod;

    if (pdsRebateAgreement.RecId
        && pdsRebateAgreement.checkRelatedRebateTransExist())
    {
        pdsRebateAgreement_ds.allowEdit(false);
        ButtonGroupItems.enabled(false);
    }
    else
    {
        pdsRebateAgreement_ds.allowEdit(true);
        ButtonGroupItems.enabled(true);

        if (custRebateCodeAllFlag)
        {
            pdsRebateAgreement.PdsCustRebateRelation = '';
        }

        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, PdsCustRebateRelation)).enabled(!custRebateCodeAllFlag);
        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, PdsCustRebateRelation)).skip(custRebateCodeAllFlag);

        if (itemRebateCodeAllOrCustomFlag)
        {
            pdsRebateAgreement.PdsItemRebateRelation = '';
        }

        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, PdsItemRebateRelation)).enabled(!itemRebateCodeAllOrCustomFlag);
        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, PdsItemRebateRelation)).skip(itemRebateCodeAllOrCustomFlag);
        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateCustomizedPeriodType)).enabled(cumulateSalesByCustPeriodFlag);
        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateCustomizedPeriodType)).skip(!cumulateSalesByCustPeriodFlag);
    }

    element.updateDesign(InventDimFormDesignUpdate::Active);
    element.setAvailFields();
}

