public void setAvailFields()
{
    boolean workflowInReview;
    boolean enableValidation;
    boolean genericCurrencyFlag = pdsRebateAgreement.RebateGenericCurrency;
    WorkflowTrackingStatusTable workflowTrackingStatus;

    TabPageSelectionDimensions.visible(pdsRebateAgreement.PdsItemRebateCode == PdsRebateItemCode::Selection);
    TabPageAgreementDimensions.visible(pdsRebateAgreement.PdsItemRebateCode != PdsRebateItemCode::Selection);
    if (SalesParameters::find().GenericCurrency)
    {
        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateGenericCurrency)).enabled(true);
    }
    else
    {
        pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateGenericCurrency)).enabled(false);
    }

    pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateCurrency)).enabled(!PdsRebateAgreement.RebateGenericCurrency);

    if (pdsRebateAgreement.PdsItemRebateCode != PdsRebateItemCode::Selection)
    {
        PdsRebateItemList.enabled(false);
        PdsRebateItemList.visible(false);
    }
    else
    {
        PdsRebateItemList.enabled(true);
        PdsRebateItemList.visible(true);
    }

    if (PdsRebateAgreement.PdsRebateLineBreakType == PdsRebateLineBreakType::Amount)
    {
        PdsRebateAgreementLine_RebateFromQty.visible(false);
        PdsRebateAgreementLine_RebateToQty.visible(false);
        PdsRebateAgreementLine_RebateFromAmount.visible(true);
        PdsRebateAgreementLine_RebateToAmount.visible(true);

        if (PdsRebateAgreementLine.RecId == 0)
        {
            PdsRebateAgreementLine.RebateFromQty = 0;
            PdsRebateAgreementLine.RebateToQty = 0;
        }
    }
    else if (PdsRebateAgreement.PdsRebateLineBreakType == PdsRebateLineBreakType::Quantity)
    {
        PdsRebateAgreementLine_RebateFromQty.visible(true);
        PdsRebateAgreementLine_RebateToQty.visible(true);
        PdsRebateAgreementLine_RebateFromAmount.visible(false);
        PdsRebateAgreementLine_RebateToAmount.visible(false);

        if (PdsRebateAgreementLine.RecId == 0)
        {
            PdsRebateAgreementLine.RebateFromAmount = 0;
            PdsRebateAgreementLine.RebateToAmount = 0;
        }
    }

    if (!WorkFlowTable::findActiveTemplate(workFlowTypeStr(MCRCustRebateAppr)).RecId != 0)
    {
        workflowEnabled = false;
        enableValidation = true;
    }
    else
    {
        if (element.canSubmitToWorkflow())
        {
            workflowEnabled = true;
            enableValidation = false;
        }
        else
        {
            workflowTrackingStatus = Workflow::findTrackingStatusForDocument(PdsRebateAgreement);
            if (workflowTrackingStatus)
            {
                // Record has a tracking status and been approved yet.
                if (workflowTrackingStatus.TrackingStatus == WorkflowTrackingStatus::Completed)
                {
                    workflowEnabled = true;
                    enableValidation = true;
                }
                else
                {
                    // Record has a tracking status but has not been approved yet.
                    workflowEnabled = true;
                    enableValidation = false;
                }
            }
            else
            {
                // Record does not have a tracking status.
                workflowEnabled = true;
                enableValidation = true;
            }
        }
    }

    // can only approve an agreement with a line
    pdsRebateAgreementValidate.enabled(pdsRebateAgreementLine.RecId != 0
                                       && enableValidation);

    // If workflow is in the submitted status, then lock down the record
    // so it can't be edited.
    if (workflowEnabled)
    {
        workflowInReview = pdsRebateAgreement.TAMCustRebateApprovalStatus == TAMCustRebateApprovalStatus::Submitted
            || pdsRebateAgreement.checkRelatedRebateTransExist();

        pdsRebateAgreement_ds.allowEdit(!workflowInReview);
        pdsRebateAgreementLine_ds.allowEdit(!workflowInReview);
        pdsRebateAgreement_ds.allowDelete(!workflowInReview);
        pdsRebateAgreementLine_ds.allowCreate(!workflowInReview);
    }
}

public void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm       inventDimParmEnabled;
    InventDimGroupSetup inventDimGroupSetup;
    InventTable         inventTable;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetUp)
            {
                inventDimFormSetUp = InventDimCtrl_Frm_PdsRebateAgreement::newFromForm(element);
                inventDimFormSetUp.defaultInventDimParm();
                inventDimFormSetUp.inventDimEnable();
                inventDimFormSetUp.formSetControls(true);
            }
            break;

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetUp.formActiveSetup();
            if (pdsRebateAgreement.PdsItemRebateCode == PdsRebateItemCode::Table
                && pdsRebateAgreement.PdsItemRebateRelation)
            {
                inventTable = InventTable::find(pdsRebateAgreement.PdsItemRebateRelation);
            }
            else if (pdsRebateAgreement.PdsItemRebateCode == PdsRebateItemCode::Selection
                  && pdsRebateItem.ItemID)
            {
                inventTable = InventTable::find(pdsRebateItem.ItemID);
            }

            inventDimGroupSetup = InventDimGroupSetup::newDimensionGroups(inventTable.productDimensionGroup(),
                                                                              inventTable.storageDimensionGroup(),
                                                                              inventTable.trackingDimensionGroup());
            inventDimGroupSetup.inventDimParmActive(inventDimParmEnabled);

            inventDimFormSetUp.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetup.formSetControls(true);
            break;
    }
}

public void write()
{
    DialogButton            dialogButton;

    if ((pdsRebateAgreement.RebateCurrency != pdsRebateAgreement.orig().RebateCurrency
        || pdsRebateAgreement.PdsRebateUnit != pdsRebateAgreement.orig().PdsRebateUnit)
        && pdsRebateAgreement.checkIfLineExists(false))
    {
        if (pdsRebateAgreement.RebateCurrency != pdsRebateAgreement.orig().RebateCurrency)
        {
            dialogButton = Box::yesNoCancel(strFmtLB("@SYP4881124"), DialogButton::Yes);
            switch (dialogButton)
            {
                case DialogButton::Yes:
                    pdsRebateAgreement.convertCurrencyCode();
                    break;
                case DialogButton::No: //do nothing. currency will be updated in the table method
                    break;
                case DialogButton::Cancel:
                    pdsRebateAgreement.RebateCurrency = pdsRebateAgreement.orig().RebateCurrency; //restore original currency
                    if (pdsRebateAgreement.RebateGenericCurrency
                     && !pdsRebateAgreement.orig().RebateGenericCurrency)
                    {
                        pdsRebateAgreement.RebateGenericCurrency = NoYes::No;
                    }
                    pdsRebateAgreement_ds.refresh();
                    return;
            }
        }

        if (pdsRebateAgreement.PdsRebateUnit != pdsRebateAgreement.orig().PdsRebateUnit)
        {
            warning("@SYP4881125");
        }
    }

    inventDim_ds.write();

    pdsRebateAgreement.InventdimId = InventDim::findOrCreate(inventDim).InventDimId;

    super();

    element.updateWorkflowControls();
    element.setAvailFields();
}

