}

public void executeQuery()
{
    this.buildRebateAgreementQuery();

    super();
}

		/// <summary>
        ///		Builds a query for <c>PdsRebateAgreement</c> record.
        /// </summary>
        [Replaceable]
        protected void buildRebateAgreementQuery()
        {
            QueryBuildDataSource    queryBuildDataSource;
            QueryBuildRange         queryBuildRange;

            queryBuildDataSource = this.query().dataSourceTable(tablenum(PdsRebateAgreement));

            if ((element.args().parmEnumType() == enumNum(PdsRebateValidAgreements))
             && (element.args().parmEnum()     == PdsRebateValidAgreements::Yes))
            {
                this.query().validTimeStateAsOfDate(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
            }
            else
            {
                queryBuildDataSource.clearRanges();
            }

            queryBuildRange = queryBuildDataSource.addRange(fieldNum(PdsRebateAgreement, PdsRebateProgramType));
            queryBuildRange.value(SysQuery::valueNot(PdsRebateProgramType::BillBack));
        }

public void research(boolean _retainPosition = false)
{
    if (_retainPosition)
    {
        pdsRebateAgreement_ds.reread();
        pdsRebateAgreement_ds.refresh();
        pdsRebateAgreement_ds.rereadReferenceDataSources();
    }
    else
    {
        super();
    }
}

