public boolean validate()
{
    PdsRebate   pdsRebate;
    boolean     ret;

    ret = super();

    if (ret)
    {
        pdsRebate = pdsRebate::find(PdsRebateAgreement.PdsRebateType);

        if (pdsRebate.PdsRebateProgramType == PdsRebateProgramType::BillBack)
        {
            throw error("@MCR4410222");
        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(PdsRebate), _formControl);

    Query query = new Query();
    QueryBuildDataSource source;
    QueryBuildRange rangeRebateProgramType;

    source = query.addDataSource(tableNum(PdsRebate));
    rangeRebateProgramType = source.addRange(fieldNum(PdsRebate, PdsRebateProgramType));
    rangeRebateProgramType.value(SysQuery::valueNot(PdsRebateProgramType::BillBack));

    sysTableLookup.addLookupfield(fieldNum(PdsRebate, PdsRebateType));
    sysTableLookup.addLookupfield(fieldNum(PdsRebate, Description));
    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

