void clicked()
{
    FormRun formRun;
    Args    args = new Args();

    args.record(pdsRebateAgreement);
    args.caller(element);

    formRun = new MenuFunction(menuitemDisplayStr(TAMRetailAddItems), MenuItemType::Display).create(args);
    if (formRun)
    {
        formRun.run();
        formRun.wait();
        PdsRebateItem_ds.executeQuery();
    }
}
