[Form]
public class PdsRebateTable extends FormRun
{
    CustTransOpen       custTransOpen;
    LedgerJournalTrans  ledgerJournalTrans;
    QueryBuildRange     qbrpdsRebateProgramId;

    PdsRebateType       lastpdsRebateProgramId_Rebate,
                        lastpdsRebateProgramId_TMA,
                        lastpdsRebateProgramId_BillBack;

    container                   pdsDateRanges;

    boolean                     listPageEditMode;
    PdsRebateProgramType        pdsRebateProgramTypeFilter;
    boolean                     useRebateProgramTypeFilter;

    #Define.CurrentVersion(1)
    #Localmacro.CurrentList
        lastpdsRebateProgramId_Rebate,
        lastpdsRebateProgramId_TMA
    #endMacro

}

public void clearMarked()
{
    pdsRebateTable_ds.first();

    pdsRebateTable.PdsMarkForCredit = NoYes::No;
    pdsRebateTable.setCorrectedRebateAmount();

    while (pdsRebateTable_ds.next())
    {
        pdsRebateTable.PdsMarkForCredit = NoYes::No;
        pdsRebateTable.setCorrectedRebateAmount();
    }

    pdsRebateTable_ds.research();
}

public void enableButtons()
{
    if (pdsRebateTable.pdsRebateAgreement().PdsRebateProgramType == PdsRebateProgramType::BillBack)
    {
        pdsRebateAgreement.visible(false);
    }

    pdsRebateAgreement.enabled(true);

    if (pdsRebateProgramId.text())
    {
        createManualRebates.enabled(true);
        processManualRebates.enabled(true);
    }
    else
    {
        createManualRebates.enabled(false);
        processManualRebates.enabled(false);
    }
}

public void init()
{
    listPageEditMode = element.args().menuItemName() == menuitemDisplayStr(PdsRebateTableEdit);

    if (element.args().parmEnumType() && element.args().parmEnumType() == enumNum(PdsRebateProgramType))
    {
        useRebateProgramTypeFilter = true;
        pdsRebateProgramTypeFilter = element.args().parmEnum();
    }

    super();

    if (element.args().dataset() == tableNum(LedgerJournalTrans))
    {
        pdsMarkForCreditTotalAmt_Grp.visible(false);
        pdsRebateProgramId.visible(false);
        createCreditNote.visible(false);
        passToAP.visible(false);
        clearMarked.visible(false);
    }
    else if (element.args().dataset() == tableNum(CustTransOpen))
    {
        custTransOpen = element.args().record();

        cumulate.visible(false);
        process.visible(false);

        this.design().viewEditMode(ViewEditMode::Auto);
        pdsRebateTable_DS.object(fieldNum(PdsRebateTable, PdsMarkForCredit)).allowEdit(true);
        pdsRebateTable_DS.object(fieldNum(PdsRebateTable, PdsCorrectedRebateAmt)).allowEdit(true);
    }
    else
    {
        overview.visible(!listPageEditMode);

        pdsMarkForCreditTotalAmt_Grp.visible(false);
        pdsRebateProgramId.visible(false);
        createCreditNote.visible(false);
        passToAP.visible(false);
        clearMarked.visible(false);
    }

    if (element.args().caller())
    {
        cumulate.visible(false);
        process.visible(false);
    }

    if (pdsRebateProgramTypeFilter == PdsRebateProgramType::TMA)
    {
        createManualRebates.visible(true);
        processManualRebates.visible(true);
        deleteManualRebates.visible(true);
    }

    xSysLastValue::getLast(this);
}

public container pack()
{
    if (element.args().parmEnum()
        && element.args().parmEnum() == PdsRebateProgramType::TMA)
    {
        return [#CurrentVersion,
                lastpdsRebateProgramId_Rebate,
                pdsRebateProgramId.text()
                ];
    }
    else if (element.args().parmEnum()
        && element.args().parmEnum() == PdsRebateProgramType::Rebate)
    {
        return [#CurrentVersion,
                pdsRebateProgramId.text(),
                lastpdsRebateProgramId_TMA
                ];
    }
    else if (element.args().parmEnum()
        && element.args().parmEnum() == PdsRebateProgramType::BillBack)
    {
        return [#CurrentVersion,
                pdsRebateProgramId.text(),
                lastpdsRebateProgramId_BillBack
                ];
    }
    else
    {
        return [#CurrentVersion,
                lastpdsRebateProgramId_Rebate,
                lastpdsRebateProgramId_TMA
                ];
    }
}

public str paymentReference()
{
    str paymentRef;

    if (element.args().dataset() == tableNum(CustTransOpen))
    {
        paymentRef = element.args().caller().ledgerJournalTrans_PaymentReference();
    }

    return paymentRef;
}

public void processManualRebates()
{
    PdsRebateTable::processManualRebates(
        element.args().caller().custAccount(), pdsRebateProgramId.text());
    pdsRebateTable_ds.executeQuery();
}

public void refreshRecord()
{
    PdsRebateTable pdsRebateTable_tmp;

    pdsRebateTable_tmp = pdsRebateTable.data();

    pdsRebateTable_ds.research();

    pdsRebateTable_ds.positionToRecord(pdsRebateTable_tmp);
}

public void setPdsRebateProgramType()
{
    PdsRebateTable  callerRebateTable;

    if (listPageEditMode)
    {
        callerRebateTable = element.args().record();
        pdsRebateTable.data(callerRebateTable);
        element.refreshRecord();
    }
    else if (element.args().dataset() == tableNum(CustTransOpen))
    {
        if (pdsRebateProgramId.text())
        {
            qbrpdsRebateProgramId.value(pdsRebateProgramId.text());
        }
        else
        {
            qbrpdsRebateProgramId.value(
            SysQuery::
            valueUnlimited()
            );
        }

        pdsRebateTable_ds.executeQuery();
    }

    element.enableButtons();
}

public boolean unpack(container _packedClass)
{
    Integer version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion :
            [version,#CurrentList] = _packedClass;
            break;

        default :
            return false;
    }

    if (element.args().parmEnum()
        && element.args().parmEnum() == PdsRebateProgramType::TMA)
    {
        pdsRebateProgramId.text(lastpdsRebateProgramId_TMA);
    }
    else if (element.args().parmEnum()
        && element.args().parmEnum() == PdsRebateProgramType::Rebate)
    {
        pdsRebateProgramId.text(lastpdsRebateProgramId_Rebate);
    }
    else if (element.args().parmEnum()
        && element.args().parmEnum() == PdsRebateProgramType::BillBack)
    {
        pdsRebateProgramId.text(lastpdsRebateProgramId_BillBack);
    }

    return true;
}

public void updatepdsRebateForCredit(
    PdsStartDate    _pdsStartDate,
    PdsEndDate      _pdsEndDate)
{
    if (_pdsStartDate && _pdsEndDate)
    {
        if (pdsRebateTable.PdsProcessDate >= _pdsStartDate
            && pdsRebateTable.PdsProcessDate <= _pdsEndDate
            && (pdsRebateTable.PdsRebateStatus == PdsRebateStatus::MarkForCredit
                || pdsRebateTable.PdsRebateStatus == PdsRebateStatus::Calculated)
            && pdsRebateTable.PdsMarkForCredit == NoYes::No)
        {
            pdsRebateTable.PdsMarkForCredit = NoYes::Yes;
        }
    }
}

public boolean isDefaultDimensionEditable()
{
    Map     ledgerDimensionSpecifiers;

    if (this.isRebateEditable())
    {
        ledgerDimensionSpecifiers = LedgerDimensionDefaultingEngine::getLedgerDimensionSpecifiers(pdsRebateTable.LedgerDimension, true);
        return ledgerDimensionSpecifiers.empty();
    }

    return false;
}

public boolean isRebateEditable()
{
    return pdsRebateTable.PdsRebateStatus != PdsRebateStatus::Processed &&
           pdsRebateTable.PdsRebateStatus != PdsRebateStatus::MarkForCredit &&
           PdsRebateTable.PdsRebateStatus != PdsRebateStatus::Approved;
}

private void filterGridByMarked(boolean _onlyMarked)
{
    QueryBuildDataSource pdsRebateTableQBDS    = pdsRebateTable_ds.queryBuildDataSource();
    QueryBuildRange      pdsMarkForCreditRange = SysQuery::findOrCreateRange(pdsRebateTableQBDS, fieldNum(PdsRebateTable, PdsMarkForCredit));

    if (_onlyMarked)
    {
        pdsMarkForCreditRange.value(queryValue(NoYes::Yes));
    }
    else
    {
        pdsMarkForCreditRange.value(SysQueryRangeUtil::valueUnlimited());
    }

    pdsRebateTable_ds.executeQuery();
}

private void markSelected()
{
    PdsRebateTable pdsRebateTableLocal = pdsRebateTable_ds.getFirst(true);

    while(pdsRebateTableLocal)
    {
        if (!pdsRebateTableLocal.PdsMarkForCredit)
        {
            pdsRebateTableLocal.PdsMarkForCredit = NoYes::Yes;
            pdsRebateTableLocal.update();
        }
        pdsRebateTableLocal = pdsRebateTable_ds.getNext();
    }

    pdsRebateTable_ds.executeQuery();
}

public int active()
{
    int ret;
    boolean isDefaultDimensionEditable;

    ret = super();

    element.enableButtons();

    isDefaultDimensionEditable = element.isDefaultDimensionEditable();

    DimensionEntryControl.allowEdit(isDefaultDimensionEditable);

    DimensionEntryControlOffset.allowEdit(isDefaultDimensionEditable);

    SalesTax.allowEdit(element.isRebateEditable());

    return ret;
}

//Deviation Documented
public void init()
{
    QueryBuildDataSource    qbdsPdsRebateTable;
    QueryBuildDataSource    qbdsPdsRebate;

    super();

    qbdsPdsRebateTable = this.queryBuildDataSource();
    qbdsPdsRebateTable.clearDynalinks();
    qbrpdsRebateProgramId = this.query().dataSourceNo(1).addRange(fieldNum(PdsRebateTable, PdsRebateType));
    if (element.args().dataset() == tableNum(CustTransOpen))
    {
        qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, PdsPaymtType)).value(SysQuery::valueNot(PdsPaymtType::Freight));

        if (element.args().caller().custAccount())
        {
            qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, CustAccount)).value(element.args().caller().custAccount());
        }
        else
        {
            qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, CustAccount)).value(SysQuery::valueNotEmptyString());
        }

        qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, PdsRebateStatus)).value(
            strFmt('%1,%2,%3', int2str(PdsRebateStatus::Calculated), int2str(PdsRebateStatus::MarkForCredit), int2str(PdsRebateStatus::Free)));

        if (useRebateProgramTypeFilter)
        {
            qbdsPdsRebate = qbdsPdsRebateTable.addDataSource(tableNum(PdsRebate));

            qbdsPdsRebate.joinMode(JoinMode::InnerJoin);
            qbdsPdsRebate.addLink(fieldNum(PdsRebateTable, PdsRebateType),
                fieldNum(PdsRebate, PdsRebateType));
            qbdsPdsRebate.addRange(fieldNum(PdsRebate, PdsRebateProgramType)).value(SysQuery::value(pdsRebateProgramTypeFilter));
        }
    }
    else if (element.args().dataset() == tableNum(LedgerJournalTrans))
    {
        ledgerJournalTrans = element.args().record();

        qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, CustAccount)).value(CustTable::find(LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(ledgerJournalTrans.LedgerDimension)).AccountNum);
    }
    else if (element.args().dataset() == tableNum(SalesRebateStaging))
    {
        SalesRebateStaging salesRebateStaging = element.args().record();

        qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, PdsRebateId)).value(salesRebateStaging.RebateId);
    }
    else if (element.args().dataset() == tableNum(SalesTable))
    {
        SalesTable salesTable = element.args().record();
        qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, SalesId)).value(salesTable.SalesId);
        qbdsPdsRebateTable.addRange(fieldNum(PdsRebateTable, SalesInvoicedQty)).value(strFmt('((%1.%2 != 0) || (%1.%3 != 0))',
                                                                                     qbdsPdsRebateTable.name(),
                                                                                     fieldStr(PdsRebateTable, SalesInvoicedQty),
                                                                                     fieldStr(PdsRebateTable, PdsCWSalesInvoicedQty)));

    }
}

