    [Form]
    public class PlSADFinanceTable extends FormRun
    {
        Args                                args;
        LedgerJournalTable                  ledgerJournalTable;
        boolean isTaxIntegrationEnabled = Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::SADDocument);

}

void calcTaxAmount(PlSADFinanceLines _sADFinanceLines)
{
    AmountMST amount;

    if (_sADFinanceLines.TaxCode)
    {
        amount                      = _sADFinanceLines.SADBaseAmount + _sADFinanceLines.SADDuty1Rate + _sADFinanceLines.SADDuty2Rate;
        _sADFinanceLines.TaxAmount  = round(amount * TaxData::percent(sADFinanceLines.TaxCode,sADFinanceLines.SADDate, amount) / 100, 0.01);
    }
    else
    {
        _sADFinanceLines.TaxAmount = 0;
    }
}

public void init()
{
    super();

    args = this.args();
    if (args.dataset() == tablenum(LedgerJournalTable))
    {
        generateTrans.enabled(true);
        ledgerJournalTable = args.record();
        if (isTaxIntegrationEnabled)
        {
            TaxTransSource.visible(true);
            LineTaxTransSource.visible(true);
            SADFinanceLines_ds.object(fieldNum(PlSADFinanceLines, TaxGroup)).visible(true);
            SADFinanceLines_ds.object(fieldNum(PlSADFinanceLines, TaxItemGroup)).visible(true);

            SADFinanceLines_ds.object(fieldNum(PlSADFinanceLines, TaxAmount)).visible(false);
            SADFinanceLines_ds.object(fieldNum(PlSADFinanceLines, TaxCode)).visible(false);
        }
    }
    else
    {
        generateTrans.enabled(false);
    }
}

